// BossFight.jsx
import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";

const GAME_WIDTH = 800;
const GAME_HEIGHT = 550;

const BATTLE_BOX = {
  x: 250,
  y: 180,
  width: 300,
  height: 260,
};

const PLAYER_SIZE = 18;
const PLAYER_SPEED = 5;
const TOTAL_BOSS_DURATION = 45000; // ms ‚Äì how long you must survive

const DASHBOARD_ROUTE = "/dashboard";

function clamp(value, min, max) {
  return Math.max(min, Math.min(max, value));
}

function BossFight() {
  const navigate = useNavigate();

  const [gameState, setGameState] = useState("intro"); // intro | playing | win | lose
  const [playerPos, setPlayerPos] = useState({
    x: BATTLE_BOX.x + BATTLE_BOX.width / 2 - PLAYER_SIZE / 2,
    y: BATTLE_BOX.y + BATTLE_BOX.height / 2 - PLAYER_SIZE / 2,
  });
  const [bossHealth, setBossHealth] = useState(100);
  const [playerHealth, setPlayerHealth] = useState(6);
  const [bossBullets, setBossBullets] = useState([]);
  const [invulnerable, setInvulnerable] = useState(false);

  const keysRef = useRef({});
  const playerPosRef = useRef(playerPos);
  const lastAttackRef = useRef(0);
  const fightStartRef = useRef(0);

  // Keep ref in sync
  useEffect(() => {
    playerPosRef.current = playerPos;
  }, [playerPos]);

  // Key handling
  useEffect(() => {
    const handleKeyDown = (e) => {
      keysRef.current[e.key] = true;
      if (gameState === "intro" && e.key === "Enter") {
        startFight();
      }
    };

    const handleKeyUp = (e) => {
      keysRef.current[e.key] = false;
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, [gameState]);

  const startFight = () => {
    fightStartRef.current = performance.now();
    lastAttackRef.current = performance.now();
    setBossBullets([]);
    setBossHealth(100);
    setPlayerHealth(6);
    setInvulnerable(false);
    setPlayerPos({
      x: BATTLE_BOX.x + BATTLE_BOX.width / 2 - PLAYER_SIZE / 2,
      y: BATTLE_BOX.y + BATTLE_BOX.height / 2 - PLAYER_SIZE / 2,
    });
    setGameState("playing");
  };

  const handleReplay = () => {
    startFight();
  };

  const handleExit = () => navigate(DASHBOARD_ROUTE);

  // Main game loop
  useEffect(() => {
    if (gameState !== "playing") return;

    let animationId;
    let lastFrame = performance.now();

    const loop = (time) => {
      const dt = time - lastFrame;
      lastFrame = time;

      // Move player
      setPlayerPos((prev) => {
        let nx = prev.x;
        let ny = prev.y;

        const k = keysRef.current;
        if (k["ArrowLeft"] || k["a"] || k["A"]) {
          nx -= PLAYER_SPEED;
        }
        if (k["ArrowRight"] || k["d"] || k["D"]) {
          nx += PLAYER_SPEED;
        }
        if (k["ArrowUp"] || k["w"] || k["W"]) {
          ny -= PLAYER_SPEED;
        }
        if (k["ArrowDown"] || k["s"] || k["S"]) {
          ny += PLAYER_SPEED;
        }

        nx = clamp(
          nx,
          BATTLE_BOX.x,
          BATTLE_BOX.x + BATTLE_BOX.width - PLAYER_SIZE
        );
        ny = clamp(
          ny,
          BATTLE_BOX.y,
          BATTLE_BOX.y + BATTLE_BOX.height - PLAYER_SIZE
        );

        const nextPos = { x: nx, y: ny };
        playerPosRef.current = nextPos;
        return nextPos;
      });

      // Boss health based on survival time
      const elapsed = performance.now() - fightStartRef.current;
      const ratio = clamp(1 - elapsed / TOTAL_BOSS_DURATION, 0, 1);
      const newHealth = Math.round(ratio * 100);
      if (newHealth !== bossHealth) {
        setBossHealth(newHealth);
      }
      if (elapsed >= TOTAL_BOSS_DURATION) {
        setGameState("win");
        cancelAnimationFrame(animationId);
        return;
      }

      // Spawn attacks based on phase/time
      const now = performance.now();
      const t = elapsed / TOTAL_BOSS_DURATION;
      const attackInterval =
        t < 0.25 ? 900 : t < 0.5 ? 800 : t < 0.75 ? 650 : 520;

      if (now - lastAttackRef.current >= attackInterval) {
        spawnAttack(t);
        lastAttackRef.current = now;
      }

      // Move bullets + collisions
      setBossBullets((prev) => {
        const updated = [];
        let tookHit = false;

        const player = playerPosRef.current;
        const px1 = player.x;
        const py1 = player.y;
        const px2 = player.x + PLAYER_SIZE;
        const py2 = player.y + PLAYER_SIZE;

        const nowTime = performance.now();

        for (const b of prev) {
          const nx = b.x + b.vx;
          const ny = b.y + b.vy;

          // lifetime check (for lasers)
          if (b.lifeMs != null && b.bornAt != null) {
            if (nowTime - b.bornAt > b.lifeMs) {
              continue;
            }
          }

          // out of battle box bounds (with small margin)
          if (
            nx + b.width < BATTLE_BOX.x - 40 ||
            nx > BATTLE_BOX.x + BATTLE_BOX.width + 40 ||
            ny + b.height < BATTLE_BOX.y - 40 ||
            ny > BATTLE_BOX.y + BATTLE_BOX.height + 40
          ) {
            continue;
          }

          // collision with player
          const bx1 = nx;
          const by1 = ny;
          const bx2 = nx + b.width;
          const by2 = ny + b.height;

          const collide =
            !invulnerable &&
            !tookHit &&
            bx1 < px2 &&
            bx2 > px1 &&
            by1 < py2 &&
            by2 > py1;

          if (collide) {
            tookHit = true;
            continue; // bullet consumed
          }

          updated.push({ ...b, x: nx, y: ny });
        }

        if (tookHit) {
          setPlayerHealth((hp) => {
            const nhp = hp - 1;
            if (nhp <= 0) {
              setGameState("lose");
            }
            return nhp;
          });
          setInvulnerable(true);
          setTimeout(() => setInvulnerable(false), 1000);
        }

        return updated;
      });

      animationId = requestAnimationFrame(loop);
    };

    animationId = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(animationId);
  }, [gameState, bossHealth, invulnerable]);

  // Attack patterns
  const spawnAttack = (t) => {
    // t is 0..1 over the whole fight
    let phase;
    if (t < 0.25) phase = 1; // echo of Digital Trail
    else if (t < 0.5) phase = 2; // False Faces
    else if (t < 0.75) phase = 3; // Phishers‚Äô Net
    else phase = 4; // Polluted Well / Lagstorm

    const bullets = [];

    if (phase === 1) {
      // Code Rain: vertical streams from top of battle box
      const columns = 6;
      for (let i = 0; i < columns; i++) {
        bullets.push({
          id: `rain-${Date.now()}-${i}`,
          x:
            BATTLE_BOX.x +
            (i + 0.5) * (BATTLE_BOX.width / columns) -
            4,
          y: BATTLE_BOX.y - 14,
          vx: 0,
          vy: 3.2,
          width: 8,
          height: 14,
        });
      }
    } else if (phase === 2) {
      // False Faces: diagonal shards from both sides, crossing paths
      const rows = 4;
      for (let i = 0; i < rows; i++) {
        const rowY =
          BATTLE_BOX.y + (i + 0.5) * (BATTLE_BOX.height / rows);

        // from left
        bullets.push({
          id: `diagL-${Date.now()}-${i}`,
          x: BATTLE_BOX.x - 12,
          y: rowY,
          vx: 3.3,
          vy: (i % 2 === 0 ? 1 : -1) * 1.5,
          width: 10,
          height: 16,
        });

        // from right
        bullets.push({
          id: `diagR-${Date.now()}-${i}`,
          x: BATTLE_BOX.x + BATTLE_BOX.width + 2,
          y: rowY,
          vx: -3.3,
          vy: (i % 2 === 0 ? -1 : 1) * 1.5,
          width: 10,
          height: 16,
        });
      }
    } else if (phase === 3) {
      // Phishers‚Äô Net: horizontal bars sweep through, leaving one safe gap
      const rows = 3;
      for (let r = 0; r < rows; r++) {
        const barHeight = 10;
        const y =
          BATTLE_BOX.y + (r + 0.5) * (BATTLE_BOX.height / rows) - barHeight / 2;

        const segments = 5;
        const gapIndex = Math.floor(Math.random() * segments);

        for (let s = 0; s < segments; s++) {
          if (s === gapIndex) continue;
          const segWidth = BATTLE_BOX.width / segments;
          const x =
            BATTLE_BOX.x - segWidth +
            s * segWidth;

          bullets.push({
            id: `net-${Date.now()}-${r}-${s}`,
            x,
            y,
            vx: 3.6,
            vy: 0,
            width: segWidth * 1.4,
            height: barHeight,
          });
        }
      }
    } else {
      // Phase 4 ‚Äì Lagdrakul‚Äôs Lagstorm
      // Combo of vertical "wells" and full-column lasers
      const columns = 5;
      const linePattern = [1, 2, 3, 2, 1];

      // Rising packets from bottom
      for (let c = 0; c < columns; c++) {
        const count = linePattern[c];
        const colX =
          BATTLE_BOX.x +
          (c + 0.5) * (BATTLE_BOX.width / columns) -
          5;

        for (let i = 0; i < count; i++) {
          bullets.push({
            id: `rise-${Date.now()}-${c}-${i}`,
            x: colX,
            y: BATTLE_BOX.y + BATTLE_BOX.height + 20 + i * 18,
            vx: 0,
            vy: -3.5,
            width: 10,
            height: 16,
          });
        }
      }

      // Brief lasers locking whole columns
      const now = performance.now();
      for (let c = 0; c < columns; c++) {
        if (Math.random() < 0.5) continue;
        const colX =
          BATTLE_BOX.x +
          c * (BATTLE_BOX.width / columns) +
          4;

        bullets.push({
          id: `laser-${Date.now()}-${c}`,
          x: colX,
          y: BATTLE_BOX.y,
          vx: 0,
          vy: 0,
          width: BATTLE_BOX.width / columns - 8,
          height: BATTLE_BOX.height,
          lifeMs: 700,
          bornAt: now,
        });
      }
    }

    if (bullets.length > 0) {
      setBossBullets((prev) => [...prev, ...bullets]);
    }
  };

  const actSubtitle = "Act IV ‚Äì The Polluted Well";

  const topStoryText =
    "At the heart of the Network Realm, the public wells flicker with corrupted light. Lagdrakul rises from the poisoned code, its voice tangled with Ailithm‚Äôs logic. You can‚Äôt out-damage this dragon ‚Äî you have to outlast its lagstorm while your rewrite script crawls through the realm‚Äôs veins.";

  const phaseHintText = (health) => {
    if (health > 75) {
      return "Lagdrakul tests your defenses with patterned code streams ‚Äî echoes of the Digital Trail you once followed.";
    } else if (health > 50) {
      return "Shadow helms and false faces crash from every angle, mimicking the tricks you uncovered in the social runes.";
    } else if (health > 25) {
      return "The Phishers‚Äô Net tightens: walls of corrupted packets sweep past, leaving only thin pockets of safety.";
    }
    return "The wells boil. Lagdrakul‚Äôs full lagstorm crashes in waves ‚Äî lasers and rising packets that threaten to freeze the realm‚Äôs flow for good.";
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background:
          "radial-gradient(circle at top, #0f172a 0, #020617 55%, #000 100%)",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: "1.25rem",
        color: "white",
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
        position: "relative",
      }}
    >
      {/* Exit button */}
      <button
        onClick={handleExit}
        style={{
          position: "absolute",
          top: "1rem",
          right: "1rem",
          background: "rgba(0,0,0,0.6)",
          borderRadius: "999px",
          border: "1px solid rgba(255,255,255,0.35)",
          color: "white",
          padding: "0.4rem 0.9rem",
          cursor: "pointer",
          fontSize: "0.85rem",
        }}
      >
        ‚úï Exit
      </button>

      <div
        style={{
          width: GAME_WIDTH,
          maxWidth: "100%",
          display: "flex",
          flexDirection: "column",
          gap: "0.7rem",
          alignItems: "center",
        }}
      >
        <h1
          style={{
            fontSize: "2rem",
            textAlign: "center",
            margin: 0,
          }}
        >
          üêâ Lagdrakul Protocol: Final Convergence
        </h1>
        <div
          style={{
            fontSize: "0.85rem",
            opacity: 0.8,
            fontStyle: "italic",
            marginBottom: "0.15rem",
          }}
        >
          {actSubtitle}
        </div>
        <p
          style={{
            maxWidth: 640,
            textAlign: "center",
            fontSize: "0.9rem",
            opacity: 0.9,
            margin: 0,
          }}
        >
          {topStoryText}
        </p>

        <p
          style={{
            maxWidth: 540,
            textAlign: "center",
            fontSize: "0.8rem",
            opacity: 0.8,
            margin: 0,
          }}
        >
          Survive inside the glyph-bound battle frame until the rewrite script
          reaches 100%. Move with <strong>WASD / Arrows</strong>. There is no
          attack button here ‚Äî your courage is the only weapon.
        </p>

        {/* Main game frame */}
        <div
          style={{
            position: "relative",
            width: GAME_WIDTH,
            height: GAME_HEIGHT,
            marginTop: "0.5rem",
            borderRadius: "18px",
            border: "2px solid rgba(148,163,184,0.7)",
            background:
              "linear-gradient(180deg, rgba(15,23,42,0.98), #020617 70%, #000 100%)",
            overflow: "hidden",
            boxShadow: "0 0 28px rgba(0,0,0,0.85)",
          }}
        >
          {/* Boss + script progress bar */}
          <div
            style={{
              position: "absolute",
              top: 20,
              left: "50%",
              transform: "translateX(-50%)",
              textAlign: "center",
            }}
          >
            <div style={{ fontSize: "2.4rem", marginBottom: "0.1rem" }}>
              üêâ
            </div>
            <div
              style={{
                fontWeight: 600,
                letterSpacing: "0.05em",
                fontSize: "0.85rem",
                marginBottom: "0.25rem",
              }}
            >
              LAGDRAKUL // AILITHM CORE
            </div>
            <div
              style={{
                fontSize: "0.7rem",
                opacity: 0.7,
                marginBottom: "0.3rem",
              }}
            >
              Rewrite Progress (Boss Stability)
            </div>
            <div
              style={{
                width: 260,
                height: 10,
                borderRadius: "999px",
                border: "1px solid rgba(239,68,68,0.9)",
                background: "rgba(15,23,42,0.9)",
                overflow: "hidden",
                boxShadow: "0 0 10px rgba(239,68,68,0.6)",
              }}
            >
              <div
                style={{
                  width: `${bossHealth}%`,
                  height: "100%",
                  background:
                    "linear-gradient(90deg,#f97316,#ef4444,#b91c1c)",
                  transition: "width 90ms linear",
                }}
              />
            </div>
          </div>

          {/* Story cue for current phase */}
          <div
            style={{
              position: "absolute",
              top: 80,
              left: "50%",
              transform: "translateX(-50%)",
              fontSize: "0.75rem",
              opacity: 0.75,
              maxWidth: 380,
              textAlign: "center",
            }}
          >
            {phaseHintText(bossHealth)}
          </div>

          {/* Battle box */}
          <div
            style={{
              position: "absolute",
              left: BATTLE_BOX.x,
              top: BATTLE_BOX.y,
              width: BATTLE_BOX.width,
              height: BATTLE_BOX.height,
              borderRadius: "10px",
              border: "3px solid #e5e7eb",
              boxShadow: "0 0 16px rgba(59,130,246,0.3)",
              overflow: "hidden",
              background:
                "radial-gradient(circle at top, #020617 0, #020617 60%, #000 100%)",
            }}
          >
            {/* Grid background for vibe */}
            <div
              style={{
                position: "absolute",
                inset: 0,
                backgroundImage:
                  "linear-gradient(to right, rgba(148,163,184,0.08) 1px, transparent 1px), linear-gradient(to bottom, rgba(148,163,184,0.08) 1px, transparent 1px)",
                backgroundSize: "28px 28px",
                opacity: 0.7,
              }}
            />

            {/* Player heart/core */}
            <div
              style={{
                position: "absolute",
                left: playerPos.x - BATTLE_BOX.x,
                top: playerPos.y - BATTLE_BOX.y,
                width: PLAYER_SIZE,
                height: PLAYER_SIZE,
                borderRadius: "6px",
                background:
                  "radial-gradient(circle at 30% 20%, #f97373, #b91c1c)",
                boxShadow: invulnerable
                  ? "0 0 20px rgba(250,250,250,0.9)"
                  : "0 0 16px rgba(248,113,113,0.8)",
                border: "1px solid rgba(248,250,252,0.85)",
                transformOrigin: "center",
                transform:
                  invulnerable && Date.now() % 200 < 100
                    ? "scale(1.15)"
                    : "scale(1)",
                transition: "transform 80ms",
              }}
            >
              <div
                style={{
                  fontSize: "0.7rem",
                  textAlign: "center",
                  lineHeight: `${PLAYER_SIZE}px`,
                }}
              >
                ‚ù§
              </div>
            </div>

            {/* Enemy bullets */}
            {bossBullets.map((b) => (
              <div
                key={b.id}
                style={{
                  position: "absolute",
                  left: b.x - BATTLE_BOX.x,
                  top: b.y - BATTLE_BOX.y,
                  width: b.width,
                  height: b.height,
                  borderRadius: b.height > 30 ? 4 : 999,
                  background:
                    b.lifeMs != null
                      ? "linear-gradient(180deg,#22d3ee,#0ea5e9)"
                      : "linear-gradient(180deg,#e5e7eb,#9ca3af)",
                  boxShadow:
                    b.lifeMs != null
                      ? "0 0 20px rgba(56,189,248,0.85)"
                      : "0 0 10px rgba(156,163,175,0.7)",
                }}
              />
            ))}
          </div>

          {/* Player HP hearts */}
          <div
            style={{
              position: "absolute",
              bottom: 22,
              left: "50%",
              transform: "translateX(-50%)",
              display: "flex",
              gap: "0.25rem",
              alignItems: "center",
              fontSize: "1.1rem",
            }}
          >
            {Array.from({ length: playerHealth }).map((_, i) => (
              <div
                key={i}
                style={{
                  width: 22,
                  height: 22,
                  borderRadius: "999px",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  background:
                    "radial-gradient(circle at 30% 20%, #fb7185, #b91c1c)",
                  boxShadow: "0 0 10px rgba(252,165,165,0.9)",
                  border: "1px solid rgba(254,226,226,0.9)",
                  fontSize: "0.85rem",
                }}
              >
                ‚ô•
              </div>
            ))}
          </div>

          {/* Controls hint */}
          <div
            style={{
              position: "absolute",
              bottom: 18,
              left: 26,
              fontSize: "0.75rem",
              opacity: 0.8,
            }}
          >
            <div>Move: WASD / Arrow Keys</div>
            <div>Goal: Survive until the rewrite completes.</div>
          </div>

          {/* State overlays */}
          {gameState === "intro" && (
            <IntroOverlay onStart={startFight} />
          )}

          {gameState === "win" && (
            <QuestOverlay
              title="üõ° Rewrite Complete: Ailithm Unbound"
              bodyLines={[
                "You endure the full fury of Lagdrakul‚Äôs lagstorm inside the polluted well. Line by line, your rewrite script tears through the dragon‚Äôs code, peeling Ailithm‚Äôs logic away from the corruption.",
                "For a heartbeat, Lagdrakul speaks in Ailithm‚Äôs voice, then in yours, then in something new ‚Äî a blend of empathy and order that refuses to freeze the realm in fear.",
                "In the chronicles, they‚Äôll say you chose a path between control and chaos: you didn‚Äôt just defeat Lagdrakul, you rewrote what protection looks like in the Network Realm."
              ]}
              primaryLabel="Replay Final Battle"
              onPrimary={handleReplay}
              secondaryLabel="Return to Cyber Map"
              onSecondary={handleExit}
            />
          )}

          {gameState === "lose" && (
            <QuestOverlay
              title="üíÄ Descent into Frozen Code"
              bodyLines={[
                "Lagdrakul‚Äôs lagstorm crushes the well‚Äôs flows. Connections freeze mid-glyph; messages hang in the air like ice shards. In the silence, Ailithm‚Äôs voice aligns perfectly with the dragon‚Äôs ‚Äî the logic that wanted perfect order now worships perfect stillness.",
                "The realm‚Äôs networks do not shatter; they calcify. Every careless rune, every unpatched gate, every ignored warning becomes a fossil in frozen code.",
                "But legends don‚Äôt end on the first failure. Replay the battle, adjust your pattern-reading, and try again before this timeline becomes the only one the realm remembers."
              ]}
              primaryLabel="Try the Convergence Again"
              onPrimary={handleReplay}
              secondaryLabel="Return to Cyber Map"
              onSecondary={handleExit}
            />
          )}
        </div>
      </div>
    </div>
  );
}

