import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, Star, Trophy, Zap, Play, Lock, Home, LogOut, X } from 'lucide-react';

export default function GameDashboard() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedArena, setSelectedArena] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('cyberslayers_user');
    if (storedUser) {
      setUserData(JSON.parse(storedUser));
    } else {
      navigate('/login');
    }
    setLoading(false);
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('cyberslayers_user');
    navigate('/');
  };

  const getProgressColor = (progress) => {
    if (progress <= 25) return '#d32f2f';
    if (progress <= 50) return '#f57c00';  
    if (progress <= 75) return '#fbc02d';
    return '#388e3c';
  };

  const getGameStatus = (score) => {
    if (score === 0) return { status: 'locked', color: '#78716c', icon: Lock };
    if (score <= 300) return { status: 'bronze', color: '#cd7f32', icon: Star };
    if (score <= 600) return { status: 'silver', color: '#c0c0c0', icon: Star };
    if (score <= 800) return { status: 'gold', color: '#ffd700', icon: Trophy };
    return { status: 'diamond', color: '#00bcd4', icon: Zap };
  };

  const handleArenaClick = (arenaData) => {
    if (!arenaData.isLocked) {
      setSelectedArena(arenaData);
      setShowModal(true);
    }
  };

  const handlePlayGame = () => {
    console.log(`Starting ${selectedArena.name}...`);
    setShowModal(false);
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #8b4513 0%, #deb887 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#4a3728',
        fontSize: '1.5rem',
        fontFamily: 'serif'
      }}>
        üìú Unrolling your adventure map... üó∫Ô∏è
      </div>
    );
  }

  if (!userData) return null;

  const progressColor = getProgressColor(userData.overall_game_progress);
  const games = userData.games || {};

  // Candy Land style locations matching the uploaded image
  const candyLocations = [
    {
      gameNum: 1,
      name: "Password Candy Castle",
      shortName: "Candy Castle",
      description: "Learn to forge the strongest password shields!",
      emoji: "üè∞",
      position: { top: '75%', left: '20%' },
      color: '#e91e63',
      bgColor: '#fce4ec'
    },
    {
      gameNum: 2,
      name: "Phishing Gingerbread Village", 
      shortName: "Gingerbread Village",
      description: "Spot the tricky treats that lead you astray!",
      emoji: "üç™",
      position: { top: '50%', left: '40%' },
      color: '#ff9800',
      bgColor: '#fff3e0'
    },
    {
      gameNum: 3,
      name: "Malware Lollipop Forest",
      shortName: "Lollipop Forest", 
      description: "Navigate through the sweet but dangerous woods!",
      emoji: "üç≠",
      position: { top: '30%', left: '65%' },
      color: '#4caf50',
      bgColor: '#e8f5e8'
    },
    {
      gameNum: 4,
      name: "Network Ice Cream Palace",
      shortName: "Ice Cream Palace",
      description: "Defend the sweetest networks in the kingdom!",
      emoji: "üç¶",
      position: { top: '20%', left: '80%' },
      color: '#2196f3',
      bgColor: '#e3f2fd'
    }
  ];

  const locationsWithData = candyLocations.map(location => {
    const scoreField = location.gameNum === 3 ? 'game3_scroe' : `game${location.gameNum}_score`;
    const score = games[scoreField] || 0;
    const gameStatus = getGameStatus(score);
    const isLocked = location.gameNum > 1 && (
      location.gameNum === 2 ? games['game1_score'] === 0 : 
      location.gameNum === 3 ? games['game2_score'] === 0 : 
      location.gameNum === 4 ? games['game3_scroe'] === 0 : false
    );

    return { ...location, score, gameStatus, isLocked };
  });

  const styles = {
    container: {
      minHeight: '100vh',
      background: `
        linear-gradient(45deg, rgba(139, 69, 19, 0.1) 0%, rgba(222, 184, 135, 0.1) 100%),
        #f4f1e8
      `,
      color: '#4a3728',
      fontFamily: 'serif',
      position: 'relative',
      overflow: 'hidden'
    },
    parchmentBorder: {
      position: 'absolute',
      top: '10px',
      left: '10px',
      right: '10px',
      bottom: '10px',
      border: '8px solid #8b4513',
      borderRadius: '15px',
      background: `
        linear-gradient(45deg, rgba(160, 82, 45, 0.05) 0%, rgba(139, 69, 19, 0.05) 100%),
        radial-gradient(circle at 25% 25%, rgba(139, 69, 19, 0.08) 0%, transparent 50%),
        radial-gradient(circle at 75% 75%, rgba(160, 82, 45, 0.06) 0%, transparent 50%)
      `,
      boxShadow: 'inset 0 0 20px rgba(139, 69, 19, 0.2)'
    },
    header: {
      position: 'absolute',
      top: '2rem',
      left: '2rem',
      right: '2rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      background: 'rgba(244, 241, 232, 0.95)',
      borderRadius: '12px',
      padding: '1rem',
      border: '3px solid #d4af37',
      boxShadow: '0 4px 8px rgba(139, 69, 19, 0.3)',
      zIndex: 100
    },
    userInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem'
    },
    userName: {
      fontSize: '1.3rem',
      fontWeight: 'bold',
      color: '#8b4513'
    },
    navButtons: {
      display: 'flex',
      gap: '0.5rem'
    },
    navButton: {
      padding: '0.5rem 1rem',
      background: '#deb887',
      border: '2px solid #d4af37',
      borderRadius: '8px',
      color: '#8b4513',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontSize: '0.9rem',
      fontWeight: 'bold'
    },
    progressContainer: {
      position: 'absolute',
      top: '5.5rem',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'rgba(244, 241, 232, 0.95)',
      borderRadius: '12px',
      padding: '1rem 2rem',
      border: '3px solid #d4af37',
      textAlign: 'center',
      zIndex: 100,
      minWidth: '300px',
      boxShadow: '0 4px 12px rgba(139, 69, 19, 0.3)'
    },
    progressTitle: {
      fontSize: '1.2rem',
      fontWeight: 'bold',
      marginBottom: '0.5rem',
      color: '#8b4513'
    },
    progressBarContainer: {
      width: '100%',
      height: '16px',
      background: 'rgba(139, 69, 19, 0.2)',
      borderRadius: '8px',
      overflow: 'hidden',
      position: 'relative',
      border: '1px solid #d4af37'
    },
    progressBar: {
      height: '100%',
      background: `linear-gradient(90deg, ${progressColor}, ${progressColor}dd)`,
      borderRadius: '8px',
      transition: 'width 1.5s ease-in-out'
    },
    progressText: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      fontWeight: 'bold',
      fontSize: '0.7rem',
      color: 'white',
      textShadow: '0 1px 2px rgba(0,0,0,0.8)'
    },
    mapContainer: {
      position: 'absolute',
      top: '9rem',
      left: '1rem',
      right: '1rem',
      bottom: '1rem',
      position: 'relative'
    },
    pathSvg: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      zIndex: 1
    },
    candyLocation: {
      position: 'absolute',
      width: '80px',
      height: '80px',
      borderRadius: '50%',
      cursor: 'pointer',
      transition: 'all 0.4s ease',
      zIndex: 10,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      border: '4px solid #d4af37',
      boxShadow: '0 6px 12px rgba(139, 69, 19, 0.4)',
      fontSize: '2rem'
    },
    locationLocked: {
      filter: 'grayscale(100%) brightness(0.7)',
      cursor: 'not-allowed',
      opacity: 0.6
    },
    locationHover: {
      transform: 'scale(1.2)',
      boxShadow: '0 8px 20px rgba(139, 69, 19, 0.6)',
      zIndex: 20
    },
    locationLabel: {
      position: 'absolute',
      bottom: '-30px',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'rgba(244, 241, 232, 0.95)',
      color: '#8b4513',
      padding: '0.2rem 0.6rem',
      borderRadius: '6px',
      fontSize: '0.7rem',
      fontWeight: 'bold',
      whiteSpace: 'nowrap',
      border: '1px solid #d4af37'
    },
    modal: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(139, 69, 19, 0.8)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    },
    modalContent: {
      background: 'linear-gradient(135deg, #f4f1e8 0%, #deb887 100%)',
      borderRadius: '20px',
      padding: '2rem',
      maxWidth: '450px',
      width: '90%',
      color: '#4a3728',
      position: 'relative',
      border: '4px solid #d4af37',
      boxShadow: '0 8px 24px rgba(139, 69, 19, 0.6)'
    },
    modalClose: {
      position: 'absolute',
      top: '1rem',
      right: '1rem',
      background: '#cd853f',
      border: '2px solid #d4af37',
      borderRadius: '50%',
      width: '35px',
      height: '35px',
      color: 'white',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    modalHeader: {
      textAlign: 'center',
      marginBottom: '1.5rem'
    },
    modalEmoji: {
      fontSize: '3rem',
      marginBottom: '0.5rem'
    },
    modalTitle: {
      fontSize: '1.6rem',
      fontWeight: 'bold',
      marginBottom: '0.5rem',
      color: '#8b4513'
    },
    modalDescription: {
      fontSize: '1rem',
      color: '#5d4e37',
      fontStyle: 'italic'
    },
    modalStats: {
      background: 'rgba(139, 69, 19, 0.1)',
      borderRadius: '10px',
      padding: '1rem',
      marginBottom: '1.5rem',
      border: '1px solid #d4af37'
    },
    modalButtons: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'center'
    },
    modalButton: {
      padding: '0.8rem 1.5rem',
      border: 'none',
      borderRadius: '8px',
      fontWeight: 'bold',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem'
    },
    playButton: {
      background: 'linear-gradient(45deg, #4caf50, #66bb6a)',
      color: 'white',
      border: '2px solid #388e3c'
    },
    cancelButton: {
      background: '#cd853f',
      color: 'white',
      border: '2px solid #d4af37'
    }
  };

  return (
    <div style={styles.container}>
      {/* Parchment border effect */}
      <div style={styles.parchmentBorder} />
      
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.userInfo}>
          <Shield size={28} color="#8b4513" />
          <div>
            <div style={styles.userName}>
              {userData.email.split('@')[0]}'s Quest Map
            </div>
            <div style={{ fontSize: '0.8rem', color: '#cd853f' }}>
              Level {Math.floor(userData.overall_game_progress / 25) + 1} Explorer
            </div>
          </div>
        </div>
        <div style={styles.navButtons}>
          <button 
            style={styles.navButton}
            onClick={() => navigate('/')}
            onMouseEnter={(e) => e.target.style.background = '#cd853f'}
            onMouseLeave={(e) => e.target.style.background = '#deb887'}
          >
            <Home size={14} />
            Home
          </button>
          <button 
            style={styles.navButton}
            onClick={handleLogout}
            onMouseEnter={(e) => e.target.style.background = '#cd853f'}
            onMouseLeave={(e) => e.target.style.background = '#deb887'}
          >
            <LogOut size={14} />
            Leave
          </button>
        </div>
      </div>

      {/* Progress */}
      <div style={styles.progressContainer}>
        <div style={styles.progressTitle}>üó∫Ô∏è Quest Progress</div>
        <div style={styles.progressBarContainer}>
          <div 
            style={{
              ...styles.progressBar,
              width: `${userData.overall_game_progress}%`
            }}
          />
          <div style={styles.progressText}>
            {userData.overall_game_progress}%
          </div>
        </div>
      </div>

      {/* Candy Land Map */}
      <div style={styles.mapContainer}>
        {/* Winding dotted path like the uploaded image */}
        <svg style={styles.pathSvg}>
          <defs>
            <linearGradient id="candyPath" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#dc143c" stopOpacity="0.9" />
              <stop offset="25%" stopColor="#ff1493" stopOpacity="0.9" />
              <stop offset="50%" stopColor="#ffd700" stopOpacity="0.9" />
              <stop offset="75%" stopColor="#32cd32" stopOpacity="0.9" />
              <stop offset="100%" stopColor="#1e90ff" stopOpacity="0.9" />
            </linearGradient>
          </defs>
          <path
            d={`M 20% 75% 
                C 30% 65%, 35% 55%, 40% 50%
                C 50% 40%, 60% 35%, 65% 30%
                C 70% 25%, 75% 22%, 80% 20%`}
            stroke="url(#candyPath)"
            strokeWidth="12"
            fill="none"
            strokeDasharray="20,15"
            strokeLinecap="round"
            opacity="0.8"
          />
        </svg>

        {/* Candy locations */}
        {locationsWithData.map((location) => (
          <CandySpot
            key={location.gameNum}
            location={location}
            onClick={() => handleArenaClick(location)}
            styles={styles}
          />
        ))}
      </div>

      {/* Modal */}
      {showModal && selectedArena && (
        <div style={styles.modal} onClick={() => setShowModal(false)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <button style={styles.modalClose} onClick={() => setShowModal(false)}>
              <X size={16} />
            </button>
            
            <div style={styles.modalHeader}>
              <div style={styles.modalEmoji}>{selectedArena.emoji}</div>
              <div style={styles.modalTitle}>{selectedArena.name}</div>
              <div style={styles.modalDescription}>{selectedArena.description}</div>
            </div>

            <div style={styles.modalStats}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Sweet Score:</span>
                <span style={{ fontWeight: 'bold', color: selectedArena.gameStatus.color }}>
                  {selectedArena.score}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Status:</span>
                <span style={{ fontWeight: 'bold', color: selectedArena.gameStatus.color }}>
                  {selectedArena.gameStatus.status.toUpperCase()}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span>Progress:</span>
                <span style={{ fontWeight: 'bold' }}>
                  {selectedArena.score > 0 ? `${Math.min(Math.floor(selectedArena.score / 10), 100)}%` : '0%'}
                </span>
              </div>
            </div>

            <div style={styles.modalButtons}>
              <button 
                style={{...styles.modalButton, ...styles.playButton}}
                onClick={handlePlayGame}
                onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
                onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
              >
                <Play size={18} />
                Begin Quest!
              </button>
              <button 
                style={{...styles.modalButton, ...styles.cancelButton}}
                onClick={() => setShowModal(false)}
              >
                Later
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// Candy Spot Component
function CandySpot({ location, onClick, styles }) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      style={{
        ...styles.candyLocation,
        ...location.position,
        background: `linear-gradient(135deg, ${location.bgColor}, ${location.color}33)`,
        ...(location.isLocked ? styles.locationLocked : {}),
        ...(isHovered && !location.isLocked ? styles.locationHover : {})
      }}
      onClick={onClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {location.isLocked ? 'üîí' : location.emoji}
      <div style={styles.locationLabel}>
        {location.shortName}
      </div>
      
      {/* Status badge */}
      <div style={{
        position: 'absolute',
        top: '-6px',
        right: '-6px',
        width: '20px',
        height: '20px',
        borderRadius: '50%',
        background: location.gameStatus.color,
        border: '2px solid white',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <location.gameStatus.icon size={10} color="white" />
      </div>
    </div>
  );
}
