// src/pages/DailyFactModal.jsx
import React, { useState, useEffect } from "react";

const cyberFacts = [
  "Phishing emails often mimic trusted companies ‚Äî always check the sender‚Äôs address carefully.",
  "Using a password manager helps you create strong, unique passwords without memorizing them.",
  "Two-factor authentication adds an extra layer of security beyond your password.",
  "Public Wi-Fi can be risky ‚Äî avoid logging into sensitive accounts on open networks.",
  "Software updates often patch security holes ‚Äî don‚Äôt delay installing them.",
  "Cybercriminals often exploit curiosity ‚Äî think twice before clicking unexpected links.",
  "Back up your data regularly to protect against ransomware attacks.",
  "Strong passwords should be at least 12 characters with a mix of letters, numbers, and symbols.",
  "Social engineering attacks rely on human trust ‚Äî verify requests before sharing information.",
  "Lock your devices when unattended to prevent unauthorized access.",
  "Avoid reusing passwords across multiple accounts ‚Äî one breach can compromise many.",
  "Check website URLs carefully ‚Äî attackers use lookalike domains to trick users.",
  "Don‚Äôt overshare personal details on social media ‚Äî attackers can use them for scams.",
  "Use antivirus and firewall protection to reduce exposure to malware.",
  "Be cautious with email attachments ‚Äî they‚Äôre a common way to spread malware.",
  "Encrypt sensitive files before storing them in the cloud.",
  "Cybersecurity is everyone‚Äôs responsibility ‚Äî small habits make a big difference.",
  "Hackers often target outdated systems ‚Äî keep your devices updated.",
  "Always log out of accounts on shared or public computers.",
  "Think before you click ‚Äî most attacks start with a single careless action."
];

function getRandomFact(facts) {
  return facts[Math.floor(Math.random() * facts.length)];
}

export default function DailyFactModal({ onClose }) {
  const [fact, setFact] = useState(getRandomFact(cyberFacts));
  const [closing, setClosing] = useState(false);

  useEffect(() => {
    const today = new Date().toDateString();
    const lastShown = localStorage.getItem("lastFactDate");
    const lastFact = localStorage.getItem("lastFact");

    console.log("today:", today, "lastShown:", lastShown, "lastFact:", lastFact);

    if (lastShown !== today || !lastFact) {
      const newFact = getRandomFact(cyberFacts);
      setFact(newFact);
      localStorage.setItem("lastFactDate", today);
      localStorage.setItem("lastFact", newFact);
    } else {
      setFact(lastFact || getRandomFact(cyberFacts));
    }
  }, []);

  const showAnotherFact = () => {
    const newFact = getRandomFact(cyberFacts);
    setFact(newFact);
    localStorage.setItem("lastFact", newFact);
  };

  const handleClose = () => {
    setClosing(true);
    setTimeout(() => {
      onClose();
    }, 400);
  };

  return (
    <div className={`modal-overlay ${closing ? "fade-out" : "fade-in"}`}>
      <div className="modal-content">
        <h2>üîê Daily Cyber Fact</h2>
        <p>{fact}</p>
        <div className="modal-buttons">
          <button onClick={showAnotherFact}>Show me another fact</button>
          <button onClick={handleClose}>Got it!</button>
        </div>
      </div>
    </div>
  );
}
