import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, ArrowLeft, Eye, EyeOff } from 'lucide-react';

export default function LoginPage() {
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your login logic here
    console.log('Login attempt:', formData);
  };

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)',
      color: 'white',
      fontFamily: 'Arial, sans-serif',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '2rem'
    },
    backButton: {
      position: 'absolute',
      top: '2rem',
      left: '2rem',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: 'rgba(0, 0, 0, 0.3)',
      border: 'none',
      color: 'white',
      padding: '0.75rem 1rem',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      backdropFilter: 'blur(10px)'
    },
    loginCard: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(20px)',
      borderRadius: '24px',
      border: '1px solid rgba(0, 188, 212, 0.3)',
      padding: '3rem',
      width: '100%',
      maxWidth: '400px',
      boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)'
    },
    header: {
      textAlign: 'center',
      marginBottom: '2rem'
    },
    logo: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '0.5rem',
      marginBottom: '1rem'
    },
    logoText: {
      fontSize: '1.8rem',
      fontWeight: 'bold',
      background: 'linear-gradient(45deg, #00bcd4, #9c27b0)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text'
    },
    subtitle: {
      color: '#cccccc',
      fontSize: '1rem'
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '1.5rem'
    },
    inputGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '0.5rem'
    },
    label: {
      fontSize: '0.9rem',
      fontWeight: 'bold',
      color: '#00bcd4'
    },
    inputContainer: {
      position: 'relative'
    },
    input: {
      width: '100%',
      padding: '1rem',
      background: 'rgba(255, 255, 255, 0.1)',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      borderRadius: '8px',
      color: 'white',
      fontSize: '1rem',
      transition: 'all 0.3s ease',
      outline: 'none'
    },
    passwordToggle: {
      position: 'absolute',
      right: '1rem',
      top: '50%',
      transform: 'translateY(-50%)',
      background: 'none',
      border: 'none',
      color: '#999',
      cursor: 'pointer',
      transition: 'color 0.3s ease'
    },
    loginButton: {
      padding: '1rem',
      background: 'linear-gradient(45deg, #00bcd4, #2196f3)',
      border: 'none',
      borderRadius: '8px',
      color: 'white',
      fontSize: '1.1rem',
      fontWeight: 'bold',
      cursor: 'pointer',
      transition: 'transform 0.3s ease',
      boxShadow: '0 10px 30px rgba(0, 188, 212, 0.3)',
      marginTop: '1rem'
    },
    forgotPassword: {
      textAlign: 'center',
      marginTop: '1rem'
    },
    link: {
      color: '#9c27b0',
      textDecoration: 'none',
      fontSize: '0.9rem',
      cursor: 'pointer'
    },
    signupSection: {
      textAlign: 'center',
      marginTop: '2rem',
      paddingTop: '1rem',
      borderTop: '1px solid rgba(255, 255, 255, 0.1)',
      color: '#cccccc'
    }
  };

  return (
    <div style={styles.container}>
      <button 
        style={styles.backButton}
        onClick={() => navigate('/')}
        onMouseEnter={(e) => e.target.style.background = 'rgba(0, 188, 212, 0.2)'}
        onMouseLeave={(e) => e.target.style.background = 'rgba(0, 0, 0, 0.3)'}
      >
        <ArrowLeft size={20} />
        Back to Home
      </button>

      <div style={styles.loginCard}>
        <div style={styles.header}>
          <div style={styles.logo}>
            <Shield size={32} color="#00bcd4" />
            <span style={styles.logoText}>CyberSlayers</span>
          </div>
          <p style={styles.subtitle}>Welcome back, young cyber warrior! üõ°Ô∏è</p>
        </div>

        <form style={styles.form} onSubmit={handleSubmit}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              style={styles.input}
              placeholder="Enter your email"
              required
              onFocus={(e) => e.target.style.borderColor = '#00bcd4'}
              onBlur={(e) => e.target.style.borderColor = 'rgba(255, 255, 255, 0.2)'}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Password</label>
            <div style={styles.inputContainer}>
              <input
                type={showPassword ? 'text' : 'password'}
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                style={styles.input}
                placeholder="Enter your password"
                required
                onFocus={(e) => e.target.style.borderColor = '#00bcd4'}
                onBlur={(e) => e.target.style.borderColor = 'rgba(255, 255, 255, 0.2)'}
              />
              <button
                type="button"
                style={styles.passwordToggle}
                onClick={() => setShowPassword(!showPassword)}
                onMouseEnter={(e) => e.target.style.color = '#00bcd4'}
                onMouseLeave={(e) => e.target.style.color = '#999'}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>
          </div>

          <button 
            type="submit"
            style={styles.loginButton}
            onMouseEnter={(e) => e.target.style.transform = 'scale(1.02)'}
            onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
          >
            Login & Start Playing! üéÆ
          </button>
        </form>

        <div style={styles.forgotPassword}>
          <a href="#" style={styles.link}>Forgot your password?</a>
        </div>

        <div style={styles.signupSection}>
          <p>New to CyberSlayers?{' '}
            <a href="#" style={styles.link}>Create an account</a>
          </p>
        </div>
      </div>
    </div>
  );
}
