// GalagaGame.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const DASHBOARD_ROUTE = '/dashboard'; // change if your dashboard route is different

const GAME_WIDTH = 720;
const GAME_HEIGHT = 900;

const PLAYER_WIDTH = 50;
const PLAYER_HEIGHT = 40;
const PLAYER_SPEED = 6;

const BULLET_WIDTH = 6;
const BULLET_HEIGHT = 16;
const BULLET_SPEED = 10;
const ENEMY_BULLET_SPEED = 5;

const ENEMY_WIDTH = 44;
const ENEMY_HEIGHT = 36;

const TICK_MS = 16; // ~60 FPS
const PLAYER_LIVES = 5;

// Enemy types with different behaviors
const ENEMY_TYPES = {
  MALWARE: {
    name: 'ü¶†',
    color: 'linear-gradient(135deg, #dc2626, #991b1b)',
    border: '#ef4444',
    points: 100,
    speed: 2,
    canShoot: false,
    movement: 'zigzag' // Moves in zigzag pattern
  },
  PHISHING: {
    name: 'üìß',
    color: 'linear-gradient(135deg, #f59e0b, #d97706)',
    border: '#fbbf24',
    points: 150,
    speed: 1.5,
    canShoot: false,
    movement: 'dive' // Occasionally dives toward player
  },
  BAD_CONNECTION: {
    name: 'üì°',
    color: 'linear-gradient(135deg, #8b5cf6, #6d28d9)',
    border: '#a78bfa',
    points: 200,
    speed: 1,
    canShoot: true,
    movement: 'standard' // Standard left-right
  },
  MALICIOUS_ADS: {
    name: 'üí∞',
    color: 'linear-gradient(135deg, #f97316, #db2777)',
    border: '#fb7185',
    points: 250,
    speed: 2.5,
    canShoot: true,
    movement: 'erratic' // Fast and unpredictable
  }
};

// Wave configurations - 4 waves total
const WAVES = [
  {
    name: 'Malware Outbreak',
    enemies: [
      { type: 'MALWARE', count: 12, pattern: 'grid' }
    ]
  },
  {
    name: 'Phishing Campaign',
    enemies: [
      { type: 'PHISHING', count: 8, pattern: 'grid' },
      { type: 'MALWARE', count: 4, pattern: 'scattered' }
    ]
  },
  {
    name: 'Network Attack',
    enemies: [
      { type: 'BAD_CONNECTION', count: 6, pattern: 'grid' },
      { type: 'PHISHING', count: 6, pattern: 'scattered' }
    ]
  },
  {
    name: 'Full Assault',
    enemies: [
      { type: 'MALICIOUS_ADS', count: 4, pattern: 'scattered' },
      { type: 'BAD_CONNECTION', count: 4, pattern: 'grid' },
      { type: 'PHISHING', count: 4, pattern: 'scattered' },
      { type: 'MALWARE', count: 4, pattern: 'scattered' }
    ]
  }
];

// Progress meter showing wave completion and lives
function WaveMeter({ currentWave, totalWaves, lives, maxLives, score }) {
  const waveProgress = ((currentWave) / totalWaves) * 100;
  const waveName = currentWave <= totalWaves ? WAVES[currentWave - 1].name : 'Complete';

  return (
    <div
      style={{
        minWidth: "240px",
        padding: "1rem",
        borderRadius: "12px",
        border: "1px solid rgba(148,163,184,0.6)",
        background:
          "radial-gradient(circle at top, rgba(15,23,42,0.95), rgba(2,6,23,0.95))",
        boxShadow: "0 0 18px rgba(15,23,42,0.9)",
        display: "flex",
        flexDirection: "column",
        gap: "0.5rem",
      }}
    >
      <div
        style={{
          display: "flex",
          alignItems: "center",
          gap: "0.5rem",
          marginBottom: "0.25rem",
        }}
      >
        <span style={{ fontSize: "1.9rem" }}>üõ°Ô∏è</span>
        <div style={{ fontSize: "0.9rem", lineHeight: 1.2 }}>
          <div style={{ fontWeight: "600" }}>Security Defense</div>
          <div style={{ opacity: 0.75 }}>
            Wave {Math.min(currentWave, totalWaves)} / {totalWaves}
          </div>
        </div>
      </div>

      <div
        style={{
          height: "6px",
          borderRadius: "999px",
          overflow: "hidden",
          background: "rgba(30,64,175,0.65)",
          marginBottom: "0.25rem",
        }}
      >
        <div
          style={{
            width: `${waveProgress}%`,
            height: "100%",
            background: "linear-gradient(90deg, #3b82f6, #06b6d4)",
            transition: "width 300ms ease",
          }}
        />
      </div>

      <div style={{ fontSize: "0.85rem", fontWeight: "600", opacity: 0.9, marginBottom: "0.5rem" }}>
        {waveName}
      </div>

      {/* Lives display */}
      <div style={{ display: "flex", flexDirection: "column", gap: "0.35rem" }}>
        <div style={{ fontSize: "0.85rem", fontWeight: "600", opacity: 0.9 }}>
          Lives Remaining
        </div>
        <div style={{ display: "flex", gap: "0.3rem", flexWrap: "wrap" }}>
          {Array.from({ length: maxLives }).map((_, i) => (
            <div
              key={i}
              style={{
                width: "32px",
                height: "32px",
                borderRadius: "6px",
                border: "2px solid rgba(148,163,184,0.6)",
                background: i < lives ? "linear-gradient(135deg, #38bdf8, #1e40af)" : "rgba(15,23,42,0.5)",
                boxShadow: i < lives ? "0 0 10px rgba(56,189,248,0.5)" : "none",
                transition: "all 200ms ease",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontSize: "1.2rem",
              }}
            >
              {i < lives ? 'üöÄ' : ''}
            </div>
          ))}
        </div>
      </div>

      <div style={{ marginTop: "0.5rem", padding: "0.5rem", background: "rgba(59,130,246,0.1)", borderRadius: "6px", fontSize: "0.85rem" }}>
        <div style={{ fontWeight: "600", marginBottom: "0.25rem" }}>Score: {score}</div>
        <div style={{ opacity: 0.7, fontSize: "0.75rem" }}>Defend the network from cyber threats!</div>
      </div>
    </div>
  );
}

export default function GalagaGame() {
  const navigate = useNavigate();

  const [playerX, setPlayerX] = useState(GAME_WIDTH / 2 - PLAYER_WIDTH / 2);
  const [playerY, setPlayerY] = useState(GAME_HEIGHT - 100); // Player can now move vertically
  const [bullets, setBullets] = useState([]); // {x, y}
  const [enemyBullets, setEnemyBullets] = useState([]); // {x, y}
  const [enemies, setEnemies] = useState([]);
  const [keys, setKeys] = useState({ left: false, right: false, up: false, down: false, shoot: false });
  const [gameState, setGameState] = useState('playing'); // 'playing' | 'win' | 'lose'
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(PLAYER_LIVES);
  const [currentWave, setCurrentWave] = useState(1);
  const [isInvulnerable, setIsInvulnerable] = useState(false);

  const lastShotTimeRef = useRef(0);
  const shootCooldown = 250; // ms between shots
  const playerXRef = useRef(playerX);
  const playerYRef = useRef(playerY);

  // Keep refs in sync with state
  useEffect(() => {
    playerXRef.current = playerX;
    playerYRef.current = playerY;
  }, [playerX, playerY]);

  // Initialize first wave
  useEffect(() => {
    if (enemies.length === 0 && gameState === 'playing') {
      setEnemies(createWaveEnemies(currentWave - 1));
    }
  }, []);

  // Keyboard handling
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (gameState !== 'playing') return;
      
      if (e.key === 'ArrowLeft' || e.key === 'a' || e.key === 'A') {
        e.preventDefault();
        setKeys((k) => ({ ...k, left: true }));
      } else if (e.key === 'ArrowRight' || e.key === 'd' || e.key === 'D') {
        e.preventDefault();
        setKeys((k) => ({ ...k, right: true }));
      } else if (e.key === 'ArrowUp' || e.key === 'w' || e.key === 'W') {
        e.preventDefault();
        setKeys((k) => ({ ...k, up: true }));
      } else if (e.key === 'ArrowDown' || e.key === 's' || e.key === 'S') {
        e.preventDefault();
        setKeys((k) => ({ ...k, down: true }));
      } else if (e.key === ' ') {
        e.preventDefault();
        setKeys((k) => ({ ...k, shoot: true }));
      }
    };

    const handleKeyUp = (e) => {
      if (e.key === 'ArrowLeft' || e.key === 'a' || e.key === 'A') {
        setKeys((k) => ({ ...k, left: false }));
      } else if (e.key === 'ArrowRight' || e.key === 'd' || e.key === 'D') {
        setKeys((k) => ({ ...k, right: false }));
      } else if (e.key === 'ArrowUp' || e.key === 'w' || e.key === 'W') {
        setKeys((k) => ({ ...k, up: false }));
      } else if (e.key === 'ArrowDown' || e.key === 's' || e.key === 'S') {
        setKeys((k) => ({ ...k, down: false }));
      } else if (e.key === ' ') {
        setKeys((k) => ({ ...k, shoot: false }));
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [gameState]);

  // Game loop
  useEffect(() => {
    if (gameState !== 'playing') return;

    const interval = setInterval(() => {
      // Move player
      setPlayerX((prevX) => {
        let x = prevX;
        if (keys.left && !keys.right) x -= PLAYER_SPEED;
        if (keys.right && !keys.left) x += PLAYER_SPEED;
        if (x < 0) x = 0;
        if (x > GAME_WIDTH - PLAYER_WIDTH) x = GAME_WIDTH - PLAYER_WIDTH;
        return x;
      });

      // Shooting - FIXED: Uses ref to get current player position
      if (keys.shoot) {
        const now = performance.now();
        if (now - lastShotTimeRef.current > shootCooldown) {
          lastShotTimeRef.current = now;
          setBullets((prev) => [
            ...prev,
            {
              x: playerXRef.current + PLAYER_WIDTH / 2 - BULLET_WIDTH / 2,
              y: GAME_HEIGHT - PLAYER_HEIGHT - 30
            }
          ]);
        }
      }

      // Move bullets
      setBullets((prevBullets) =>
        prevBullets
          .map((b) => ({ ...b, y: b.y - BULLET_SPEED }))
          .filter((b) => b.y + BULLET_HEIGHT > 0)
      );

      // Move enemies as a block
      setEnemies((prevEnemies) => {
        if (prevEnemies.length === 0) return prevEnemies;

        // Determine boundaries
        let minX = Infinity;
        let maxX = -Infinity;
        prevEnemies.forEach((e) => {
          if (e.alive) {
            if (e.x < minX) minX = e.x;
            if (e.x > maxX) maxX = e.x;
          }
        });

        let newDirection = enemyDirection;
        let newOffsetY = enemyOffsetY;

        if (minX <= 10 && enemyDirection === -1) {
          newDirection = 1;
          newOffsetY += ENEMY_STEP_DOWN;
        } else if (maxX + ENEMY_WIDTH >= GAME_WIDTH - 10 && enemyDirection === 1) {
          newDirection = -1;
          newOffsetY += ENEMY_STEP_DOWN;
        }

        // Apply Y offset if changed
        if (newOffsetY !== enemyOffsetY) {
          setEnemyOffsetY(newOffsetY);
        }
        if (newDirection !== enemyDirection) {
          setEnemyDirection(newDirection);
        }

        const moved = prevEnemies.map((e) => ({
          ...e,
          x: e.x + ENEMY_SPEED_X * newDirection
        }));

        // Check lose condition: any enemy too low
        const reachedBottom = moved.some(
          (e) =>
            e.alive &&
            e.y + enemyOffsetY + ENEMY_HEIGHT >= GAME_HEIGHT - PLAYER_HEIGHT - 50
        );
        if (reachedBottom) {
          setGameState('lose');
        }

        return moved;
      });

      // Handle bullet/enemy collisions
      setBullets((prevBullets) => {
        let updatedEnemies = [...enemies];
        let updatedBullets = [...prevBullets];
        let collisionsDetected = false;

        updatedBullets.forEach((bullet, bulletIndex) => {
          if (!bullet) return;
          
          updatedEnemies.forEach((enemy, enemyIndex) => {
            if (!enemy.alive) return;

            const ex = enemy.x;
            const ey = enemy.y + enemyOffsetY;

            if (
              bullet.x < ex + ENEMY_WIDTH &&
              bullet.x + BULLET_WIDTH > ex &&
              bullet.y < ey + ENEMY_HEIGHT &&
              bullet.y + BULLET_HEIGHT > ey
            ) {
              // Hit: kill enemy & remove bullet
              updatedEnemies[enemyIndex] = { ...enemy, alive: false };
              updatedBullets[bulletIndex] = null;
              collisionsDetected = true;
              setScore((prev) => prev + 100);
            }
          });
        });

        if (collisionsDetected) {
          setEnemies(updatedEnemies);
        }

        const aliveEnemies = updatedEnemies.filter((e) => e.alive);
        if (aliveEnemies.length === 0) {
          setGameState('win');
        }

        // Clean bullets after collision check
        return updatedBullets.filter((b) => b !== null);
      });
    }, TICK_MS);

    return () => clearInterval(interval);
  }, [keys, gameState, enemyDirection, enemyOffsetY, enemies]);

  const handleExit = () => {
    navigate(DASHBOARD_ROUTE);
  };

  const handleRestart = () => {
    setPlayerX(GAME_WIDTH / 2 - PLAYER_WIDTH / 2);
    setBullets([]);
    setEnemies(createInitialEnemies());
    setEnemyDirection(1);
    setEnemyOffsetY(0);
    setGameState('playing');
    setScore(0);
    lastShotTimeRef.current = 0;
  };

  // Styles
  const containerStyle = {
    minHeight: '100vh',
    background:
      'radial-gradient(circle at top, #0f172a 0, #020617 50%, #000 100%)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    fontFamily:
      '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    padding: '1.5rem',
    gap: '1.25rem',
    position: 'relative'
  };

  const gameAreaStyle = {
    position: 'relative',
    width: GAME_WIDTH,
    height: GAME_HEIGHT,
    background:
      'linear-gradient(180deg, #020617 0%, #020617 60%, #000 100%)',
    border: '4px solid #4b5563',
    borderRadius: '12px',
    overflow: 'hidden',
    boxShadow: '0 0 32px rgba(0,0,0,0.9)'
  };

  const hudStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    width: GAME_WIDTH,
    fontSize: '1.1rem',
    opacity: 0.9,
    fontWeight: '500'
  };

  return (
    <div style={containerStyle}>
      {/* Exit */}
      <button
        onClick={handleExit}
        style={{
          position: 'absolute',
          top: '1rem',
          right: '1rem',
          background: 'rgba(0,0,0,0.6)',
          borderRadius: '999px',
          border: '1px solid rgba(255,255,255,0.3)',
          color: 'white',
          padding: '0.5rem 0.9rem',
          cursor: 'pointer',
          fontSize: '0.9rem',
          fontWeight: '500'
        }}
      >
        ‚úï Exit
      </button>

      <h1 style={{ fontSize: '2.5rem', textAlign: 'center', marginBottom: '0.25rem' }}>
        üöÄ Starfield Defender
      </h1>
      <p style={{ opacity: 0.8, textAlign: 'center', maxWidth: 600, fontSize: '1rem' }}>
        Move with <strong>‚Üê ‚Üí / A D</strong>, shoot with{' '}
        <strong>Space / ‚Üë / W</strong>. Clear all invaders before they reach you.
      </p>

      <div style={hudStyle}>
        <div>Score: <strong>{score}</strong></div>
        <div>
          Status:{' '}
          <strong>
            {gameState === 'playing'
              ? 'ENGAGED'
              : gameState === 'win'
              ? 'VICTORY'
              : 'CRITICAL FAILURE'}
          </strong>
        </div>
      </div>

      <div style={{ display: 'flex', gap: '1.5rem', alignItems: 'flex-start', flexWrap: 'wrap', justifyContent: 'center' }}>
        <div style={gameAreaStyle}>
          {/* Player - using same style as CyberQuest */}
          <div
            style={{
              position: 'absolute',
              width: PLAYER_WIDTH,
              height: PLAYER_HEIGHT,
              left: playerX,
              bottom: 30,
              background: 'linear-gradient(135deg, #38bdf8, #1e40af)',
              borderRadius: '8px',
              boxShadow: '0 0 20px rgba(56,189,248,0.9)',
              border: '2px solid #0ea5e9',
              transform: 'translateY(0)'
            }}
          >
            {/* Cockpit/top detail */}
            <div
              style={{
                width: 0,
                height: 0,
                borderLeft: `${PLAYER_WIDTH / 2}px solid transparent`,
                borderRight: `${PLAYER_WIDTH / 2}px solid transparent`,
                borderBottom: '14px solid #bfdbfe',
                position: 'absolute',
                top: -14,
                left: 0
              }}
            />
          </div>

          {/* Bullets */}
          {bullets.map((b, i) => (
            <div
              key={i}
              style={{
                position: 'absolute',
                width: BULLET_WIDTH,
                height: BULLET_HEIGHT,
                left: b.x,
                top: b.y,
                background:
                  'linear-gradient(180deg, #facc15, #f97316)',
                boxShadow: '0 0 12px rgba(250,204,21,0.95)',
                borderRadius: '999px'
              }}
            />
          ))}

          {/* Enemies - using same style as CyberQuest horse */}
          {enemies.map(
            (e, i) =>
              e.alive && (
                <div
                  key={i}
                  style={{
                    position: 'absolute',
                    width: ENEMY_WIDTH,
                    height: ENEMY_HEIGHT,
                    left: e.x,
                    top: e.y + enemyOffsetY,
                    background: 'linear-gradient(135deg, #f97316, #db2777)',
                    borderRadius: '6px',
                    border: '2px solid #fb7185',
                    boxShadow: '0 0 14px rgba(251,113,133,0.9)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '1.8rem'
                  }}
                >
                  üëæ
                </div>
              )
          )}

          {/* End-of-game overlay */}
          {gameState !== 'playing' && (
            <div
              style={{
                position: 'absolute',
                inset: 0,
                background: 'rgba(0,0,0,0.85)',
                backdropFilter: 'blur(4px)',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '1rem',
                textAlign: 'center',
                padding: '1.5rem'
              }}
            >
              <h2 style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>
                {gameState === 'win' ? 'üéâ VICTORY!' : 'üíÄ MISSION FAILED'}
              </h2>
              <p style={{ opacity: 0.9, fontSize: '1.1rem', maxWidth: '400px' }}>
                {gameState === 'win'
                  ? 'You cleared the wave of invaders. The network is safe... for now.'
                  : 'The invaders breached your defenses. Recalibrate and try again.'}
              </p>
              <div style={{ display: 'flex', gap: '0.75rem', marginTop: '0.75rem' }}>
                <button
                  onClick={handleRestart}
                  style={{
                    padding: '0.8rem 1.5rem',
                    borderRadius: '999px',
                    border: 'none',
                    background:
                      'linear-gradient(135deg, #3b82f6, #1e40af)',
                    color: 'white',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                    fontSize: '1rem',
                    boxShadow: '0 4px 12px rgba(59, 130, 246, 0.4)'
                  }}
                >
                  Play Again
                </button>
                <button
                  onClick={handleExit}
                  style={{
                    padding: '0.8rem 1.5rem',
                    borderRadius: '999px',
                    border: '1px solid rgba(148,163,184,0.6)',
                    background: 'rgba(15,23,42,0.9)',
                    color: 'white',
                    cursor: 'pointer',
                    fontSize: '1rem'
                  }}
                >
                  Back to Dashboard
                </button>
              </div>
            </div>
          )}
        </div>

        <WaveMeter enemiesKilled={enemiesKilled} totalEnemies={totalEnemies} />
      </div>

      {gameState === 'playing' && (
        <button
          onClick={handleRestart}
          style={{
            marginTop: '0.5rem',
            padding: '0.5rem 1.2rem',
            borderRadius: '999px',
            border: '1px solid rgba(148,163,184,0.6)',
            background: 'rgba(15,23,42,0.7)',
            color: 'white',
            fontSize: '0.9rem',
            cursor: 'pointer'
          }}
        >
          Reset Wave
        </button>
      )}
    </div>
  );
}

function createInitialEnemies() {
  const enemies = [];
  const startX = (GAME_WIDTH - (ENEMY_COLS - 1) * ENEMY_X_SPACING) / 2;

  for (let row = 0; row < ENEMY_ROWS; row++) {
    for (let col = 0; col < ENEMY_COLS; col++) {
      enemies.push({
        x: startX + col * ENEMY_X_SPACING,
        y: ENEMY_START_Y + row * ENEMY_Y_SPACING,
        alive: true
      });
    }
  }
  return enemies;
}
