import { useState, useEffect } from 'react';
import { Shield, Lock, Code, Zap, ChevronRight, PlayCircle, Users, BookOpen } from 'lucide-react';

export default function CyberSlayersHome() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);
  const [floatingElements, setFloatingElements] = useState([]);

  useEffect(() => {
    // Create floating cyber elements
    const elements = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 30 + 10,
      duration: Math.random() * 10 + 15,
      delay: Math.random() * 5
    }));
    setFloatingElements(elements);
  }, []);

  const handleMouseMove = (e) => {
    setMousePosition({
      x: (e.clientX / window.innerWidth) * 100,
      y: (e.clientY / window.innerHeight) * 100
    });
  };

  return (
    <div 
      className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-blue-900 text-white overflow-hidden relative"
      onMouseMove={handleMouseMove}
    >
      {/* Animated Background Grid */}
      <div className="absolute inset-0 opacity-20">
        <div className="absolute inset-0" style={{
          backgroundImage: `
            linear-gradient(rgba(0, 255, 255, 0.3) 1px, transparent 1px),
            linear-gradient(90deg, rgba(0, 255, 255, 0.3) 1px, transparent 1px)
          `,
          backgroundSize: '50px 50px',
          animation: 'gridMove 20s linear infinite'
        }} />
      </div>

      {/* Floating Cyber Elements */}
      {floatingElements.map((el) => (
        <div
          key={el.id}
          className="absolute rounded-full bg-cyan-400 opacity-10 blur-xl"
          style={{
            left: `${el.x}%`,
            top: `${el.y}%`,
            width: `${el.size}px`,
            height: `${el.size}px`,
            animation: `float ${el.duration}s ease-in-out infinite`,
            animationDelay: `${el.delay}s`
          }}
        />
      ))}

      {/* Matrix-style falling code */}
      <div className="absolute inset-0 opacity-5 pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute text-green-400 font-mono text-xs"
            style={{
              left: `${i * 5}%`,
              animation: `fall ${Math.random() * 10 + 10}s linear infinite`,
              animationDelay: `${Math.random() * 5}s`
            }}
          >
            {Array.from({ length: 20 }, () => 
              String.fromCharCode(Math.random() * (126 - 33) + 33)
            ).join('\n')}
          </div>
        ))}
      </div>

      {/* Navigation */}
      <nav className="relative z-50 flex justify-between items-center p-6 backdrop-blur-sm bg-black/20">
        <div className="flex items-center gap-8">
          <div className="flex items-center gap-2 text-2xl font-bold">
            <Shield className="text-cyan-400 animate-pulse" />
            <span className="bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
              CyberSlayers
            </span>
          </div>
          <div className="hidden md:flex gap-6">
            <a href="#" className="hover:text-cyan-400 transition-colors duration-300 flex items-center gap-1 group">
              <span>Home</span>
              <ChevronRight className="w-4 h-4 group-hover:translate-x-1 transition-transform" />
            </a>
            <a href="#" className="hover:text-cyan-400 transition-colors duration-300">About Us</a>
            <a href="#" className="hover:text-cyan-400 transition-colors duration-300">Games</a>
          </div>
        </div>
        <button className="px-6 py-2 bg-gradient-to-r from-cyan-500 to-purple-500 rounded-full font-bold hover:scale-110 transition-transform duration-300 shadow-lg shadow-cyan-500/50 hover:shadow-cyan-500/80">
          Login
        </button>
      </nav>

      {/* Hero Section */}
      <div className="relative z-10 container mx-auto px-6 py-12">
        <div className="grid md:grid-cols-2 gap-12 items-center">
          {/* Left Content */}
          <div className="space-y-8">
            <div className="space-y-4">
              <div className="inline-block">
                <div className="flex items-center gap-2 bg-cyan-500/20 border border-cyan-500/50 rounded-full px-4 py-2 backdrop-blur-sm animate-bounce">
                  <Zap className="w-4 h-4 text-yellow-400" />
                  <span className="text-sm font-bold text-cyan-300">Level Up Your Cyber Skills!</span>
                </div>
              </div>
              
              <h1 className="text-6xl md:text-7xl font-black leading-tight">
                <span className="block bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent animate-pulse">
                  Welcome to
                </span>
                <span className="block text-white mt-2">CyberSlayers</span>
              </h1>
              
              <p className="text-xl text-gray-300 leading-relaxed">
                üõ°Ô∏è This platform helps kids learn{' '}
                <span className="text-cyan-400 font-bold">cybersecurity</span> through{' '}
                <span className="text-purple-400 font-bold">epic adventures</span> and{' '}
                <span className="text-pink-400 font-bold">interactive challenges!</span>
              </p>
            </div>

            {/* CTA Buttons */}
            <div className="flex flex-wrap gap-4">
              <button 
                className="group px-8 py-4 bg-gradient-to-r from-cyan-500 to-blue-500 rounded-xl font-bold text-lg hover:scale-105 transition-all duration-300 shadow-2xl shadow-cyan-500/50 hover:shadow-cyan-500/80 flex items-center gap-2"
                onMouseEnter={() => setIsHovering(true)}
                onMouseLeave={() => setIsHovering(false)}
              >
                <PlayCircle className={`w-6 h-6 ${isHovering ? 'animate-spin' : ''}`} />
                Start Playing
                <ChevronRight className="w-5 h-5 group-hover:translate-x-2 transition-transform" />
              </button>
              
              <button className="px-8 py-4 border-2 border-purple-500 rounded-xl font-bold text-lg hover:bg-purple-500/20 transition-all duration-300 backdrop-blur-sm">
                Watch Demo Video
              </button>
            </div>

            {/* Stats */}
            <div className="flex gap-8 pt-4">
              {[
                { icon: Users, value: '10K+', label: 'Young Hackers' },
                { icon: Shield, value: '50+', label: 'Challenges' },
                { icon: BookOpen, value: '100%', label: 'Fun Guaranteed' }
              ].map((stat, i) => (
                <div key={i} className="text-center group hover:scale-110 transition-transform cursor-pointer">
                  <stat.icon className="w-8 h-8 text-cyan-400 mx-auto mb-2 group-hover:animate-bounce" />
                  <div className="text-2xl font-bold bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
                    {stat.value}
                  </div>
                  <div className="text-sm text-gray-400">{stat.label}</div>
                </div>
              ))}
            </div>
          </div>

          {/* Right Content - Logo & Interactive Elements */}
          <div className="relative">
            {/* Animated Rings around Logo */}
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="w-96 h-96 rounded-full border-2 border-cyan-500/30 animate-ping" />
              <div className="absolute w-80 h-80 rounded-full border-2 border-purple-500/30 animate-pulse" />
              <div className="absolute w-64 h-64 rounded-full border-2 border-pink-500/30" style={{ animation: 'spin 10s linear infinite' }} />
            </div>

            {/* Logo Container */}
            <div className="relative z-10 flex items-center justify-center">
              <div className="relative group">
                {/* Glow Effect */}
                <div className="absolute inset-0 bg-gradient-to-r from-cyan-500 to-purple-500 rounded-3xl blur-3xl opacity-50 group-hover:opacity-75 transition-opacity" />
                
                {/* Logo Card */}
                <div className="relative bg-gradient-to-br from-gray-800/90 to-gray-900/90 backdrop-blur-xl rounded-3xl p-12 border border-cyan-500/30 hover:border-cyan-500/60 transition-all duration-300 shadow-2xl">
                  {/* Placeholder for CyberSlayers Logo */}
                  <div className="w-64 h-64 bg-gradient-to-br from-cyan-400 to-purple-500 rounded-2xl flex items-center justify-center text-6xl font-black shadow-2xl relative overflow-hidden group-hover:scale-105 transition-transform">
                    <div className="absolute inset-0 bg-black/20 backdrop-blur-sm" />
                    <div className="relative z-10 text-center">
                      <Shield className="w-32 h-32 mx-auto mb-4 text-white animate-pulse" />
                      <div className="text-2xl text-white">CYBER</div>
                      <div className="text-2xl text-white">SLAYERS</div>
                    </div>
                    
                    {/* Animated corner accents */}
                    <div className="absolute top-0 left-0 w-16 h-16 border-t-4 border-l-4 border-cyan-400 animate-pulse" />
                    <div className="absolute bottom-0 right-0 w-16 h-16 border-b-4 border-r-4 border-purple-400 animate-pulse" />
                  </div>
                </div>

                {/* Floating Icons */}
                {[
                  { Icon: Lock, position: 'top-0 -left-12', color: 'text-cyan-400', delay: '0s' },
                  { Icon: Code, position: 'top-0 -right-12', color: 'text-purple-400', delay: '0.5s' },
                  { Icon: Shield, position: 'bottom-0 -left-12', color: 'text-pink-400', delay: '1s' },
                  { Icon: Zap, position: 'bottom-0 -right-12', color: 'text-yellow-400', delay: '1.5s' }
                ].map(({ Icon, position, color, delay }, i) => (
                  <div
                    key={i}
                    className={`absolute ${position} p-3 bg-gray-800/80 backdrop-blur-sm rounded-xl border border-gray-700 hover:scale-125 transition-transform cursor-pointer`}
                    style={{ animation: `float 3s ease-in-out infinite ${delay}` }}
                  >
                    <Icon className={`w-8 h-8 ${color}`} />
                  </div>
                ))}
              </div>
            </div>

            {/* Video Section */}
            <div className="mt-12 text-center">
              <div className="inline-block bg-gradient-to-r from-gray-800/90 to-gray-900/90 backdrop-blur-xl rounded-2xl p-8 border border-purple-500/30 hover:border-purple-500/60 transition-all duration-300 hover:scale-105 cursor-pointer group">
                <PlayCircle className="w-16 h-16 mx-auto mb-3 text-purple-400 group-hover:text-purple-300 group-hover:scale-110 transition-all" />
                <div className="text-lg font-bold">Video of us playing the game</div>
                <div className="text-sm text-gray-400 mt-1">See CyberSlayers in action! üéÆ</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Inline Animations */}
      <style>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(5deg); }
        }
        
        @keyframes fall {
          0% { transform: translateY(-100%); }
          100% { transform: translateY(100vh); }
        }
        
        @keyframes gridMove {
          0% { transform: translate(0, 0); }
          100% { transform: translate(50px, 50px); }
        }
      `}</style>
    </div>
  );
}
