import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, Star, Trophy, Zap, Play, Lock, Home, LogOut, X, Wifi, Code, Database, Globe } from 'lucide-react';

export default function GameDashboard() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedArena, setSelectedArena] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('cyberslayers_user');
    if (storedUser) {
      setUserData(JSON.parse(storedUser));
    } else {
      navigate('/login');
    }
    setLoading(false);
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('cyberslayers_user');
    navigate('/');
  };

  const getProgressColor = (progress) => {
    if (progress <= 25) return '#f44336';
    if (progress <= 50) return '#ff9800';  
    if (progress <= 75) return '#ffc107';
    return '#4caf50';
  };

  const getGameStatus = (score) => {
    if (score === 0) return { status: 'offline', color: '#78716c', icon: Lock };
    if (score <= 300) return { status: 'bronze', color: '#cd7f32', icon: Star };
    if (score <= 600) return { status: 'silver', color: '#c0c0c0', icon: Star };
    if (score <= 800) return { status: 'gold', color: '#ffd700', icon: Trophy };
    return { status: 'diamond', color: '#00bcd4', icon: Zap };
  };

  const handleArenaClick = (arenaData) => {
    if (!arenaData.isLocked) {
      setSelectedArena(arenaData);
      setShowModal(true);
    }
  };

  const handlePlayGame = () => {
    console.log(`Starting ${selectedArena.name}...`);
    setShowModal(false);
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#00bcd4',
        fontSize: '1.5rem',
        fontFamily: 'monospace'
      }}>
        üíª Initializing cyber network... üåê
      </div>
    );
  }

  if (!userData) return null;

  const progressColor = getProgressColor(userData.overall_game_progress);
  const games = userData.games || {};

  // Cyber/Tech themed security zones
  const securityZones = [
    {
      gameNum: 1,
      name: "Password Security Core",
      shortName: "Security Core",
      description: "Master authentication protocols and secure access systems!",
      emoji: "üõ°Ô∏è",
      icon: Shield,
      position: { top: '25%', left: '15%' },
      color: '#00bcd4',
      bgColor: 'rgba(0, 188, 212, 0.1)',
      techType: 'Authentication'
    },
    {
      gameNum: 2,
      name: "Phishing Detection Hub", 
      shortName: "Detection Hub",
      description: "Scan and identify malicious network communications!",
      emoji: "üîç",
      icon: Wifi,
      position: { top: '65%', left: '35%' },
      color: '#ff9800',
      bgColor: 'rgba(255, 152, 0, 0.1)',
      techType: 'Network Security'
    },
    {
      gameNum: 3,
      name: "Malware Analysis Lab",
      shortName: "Analysis Lab", 
      description: "Reverse engineer and neutralize cyber threats!",
      emoji: "üî¨",
      icon: Code,
      position: { top: '20%', left: '70%' },
      color: '#4caf50',
      bgColor: 'rgba(76, 175, 80, 0.1)',
      techType: 'Threat Analysis'
    },
    {
      gameNum: 4,
      name: "Network Command Center",
      shortName: "Command Center",
      description: "Monitor and defend the entire cyber infrastructure!",
      emoji: "üñ•Ô∏è",
      icon: Database,
      position: { top: '75%', left: '80%' },
      color: '#9c27b0',
      bgColor: 'rgba(156, 39, 176, 0.1)',
      techType: 'Infrastructure'
    }
  ];

  const zonesWithData = securityZones.map(zone => {
    const scoreField = zone.gameNum === 3 ? 'game3_scroe' : `game${zone.gameNum}_score`;
    const score = games[scoreField] || 0;
    const gameStatus = getGameStatus(score);
    const isLocked = zone.gameNum > 1 && (
      zone.gameNum === 2 ? games['game1_score'] === 0 : 
      zone.gameNum === 3 ? games['game2_score'] === 0 : 
      zone.gameNum === 4 ? games['game3_scroe'] === 0 : false
    );

    return { ...zone, score, gameStatus, isLocked };
  });

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #334155 100%)',
      color: 'white',
      fontFamily: '"Roboto Mono", monospace',
      padding: '1rem'
    },
    
    // Navbar
    navbar: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      background: 'rgba(0, 0, 0, 0.4)',
      backdropFilter: 'blur(20px)',
      borderRadius: '15px',
      padding: '1rem 1.5rem',
      border: '1px solid rgba(0, 188, 212, 0.3)',
      marginBottom: '1rem',
      boxShadow: '0 4px 20px rgba(0, 188, 212, 0.2)'
    },
    userInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem'
    },
    userName: {
      fontSize: '1.3rem',
      fontWeight: 'bold',
      color: '#00bcd4',
      textShadow: '0 0 10px rgba(0, 188, 212, 0.5)'
    },
    navButtons: {
      display: 'flex',
      gap: '0.5rem'
    },
    navButton: {
      padding: '0.5rem 1rem',
      background: 'rgba(0, 188, 212, 0.2)',
      border: '1px solid rgba(0, 188, 212, 0.5)',
      borderRadius: '10px',
      color: '#00bcd4',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontSize: '0.9rem',
      fontWeight: 'bold'
    },

    // Progress Bar Section
    progressSection: {
      background: 'rgba(0, 0, 0, 0.4)',
      backdropFilter: 'blur(20px)',
      borderRadius: '15px',
      padding: '1.5rem',
      border: '1px solid rgba(0, 188, 212, 0.3)',
      textAlign: 'center',
      marginBottom: '1rem',
      boxShadow: '0 4px 20px rgba(0, 188, 212, 0.2)'
    },
    progressTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '1rem',
      color: '#00bcd4',
      textShadow: '0 0 10px rgba(0, 188, 212, 0.5)'
    },
    progressBarContainer: {
      width: '100%',
      height: '20px',
      background: 'rgba(0, 0, 0, 0.6)',
      borderRadius: '10px',
      overflow: 'hidden',
      position: 'relative',
      border: '1px solid rgba(0, 188, 212, 0.3)'
    },
    progressBar: {
      height: '100%',
      background: `linear-gradient(90deg, ${progressColor}, ${progressColor}dd)`,
      borderRadius: '10px',
      transition: 'width 1.5s ease-in-out',
      boxShadow: `0 0 20px ${progressColor}44`
    },
    progressText: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      fontWeight: 'bold',
      fontSize: '0.9rem',
      color: 'white',
      textShadow: '0 1px 2px rgba(0,0,0,0.8)'
    },
    progressMessage: {
      marginTop: '0.5rem',
      fontSize: '1rem',
      opacity: 0.9,
      color: '#64b5f6'
    },

    // Dedicated Cyber Map Card
    mapCard: {
      background: `
        linear-gradient(145deg, rgba(15, 23, 42, 0.9) 0%, rgba(30, 41, 59, 0.9) 100%),
        rgba(0, 0, 0, 0.8)
      `,
      backdropFilter: 'blur(20px)',
      borderRadius: '20px',
      padding: '2rem',
      border: '2px solid rgba(0, 188, 212, 0.3)',
      boxShadow: '0 8px 32px rgba(0, 188, 212, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
      position: 'relative',
      minHeight: '500px',
      color: 'white'
    },
    mapTitle: {
      textAlign: 'center',
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#00bcd4',
      marginBottom: '2rem',
      textShadow: '0 0 20px rgba(0, 188, 212, 0.6)',
      letterSpacing: '2px'
    },
    mapContainer: {
      position: 'relative',
      height: '400px',
      background: `
        radial-gradient(circle at 15% 25%, rgba(0, 188, 212, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 35% 65%, rgba(255, 152, 0, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 70% 20%, rgba(76, 175, 80, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 80% 75%, rgba(156, 39, 176, 0.05) 0%, transparent 50%),
        linear-gradient(45deg, rgba(0, 0, 0, 0.2) 0%, rgba(15, 23, 42, 0.2) 100%)
      `,
      borderRadius: '15px',
      border: '1px dashed rgba(0, 188, 212, 0.4)',
      overflow: 'hidden',
      boxShadow: 'inset 0 2px 10px rgba(0, 0, 0, 0.3)'
    },
    pathSvg: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      zIndex: 2
    },
    securityZone: {
      position: 'absolute',
      width: '100px',
      height: '100px',
      borderRadius: '50%',
      cursor: 'pointer',
      transition: 'all 0.4s ease',
      zIndex: 10,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      border: '3px solid rgba(0, 188, 212, 0.6)',
      boxShadow: '0 0 20px rgba(0, 188, 212, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
      fontSize: '2.5rem'
    },
    zoneLocked: {
      filter: 'grayscale(100%) brightness(0.5)',
      cursor: 'not-allowed',
      opacity: 0.6,
      border: '3px solid rgba(120, 113, 108, 0.6)'
    },
    zoneHover: {
      transform: 'scale(1.15)',
      boxShadow: '0 0 30px rgba(0, 188, 212, 0.8), inset 0 1px 0 rgba(255, 255, 255, 0.2)',
      zIndex: 20
    },
    zoneLabel: {
      position: 'absolute',
      bottom: '-40px',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'rgba(0, 0, 0, 0.8)',
      color: '#00bcd4',
      padding: '0.3rem 0.8rem',
      borderRadius: '8px',
      fontSize: '0.8rem',
      fontWeight: 'bold',
      whiteSpace: 'nowrap',
      border: '1px solid rgba(0, 188, 212, 0.5)',
      textAlign: 'center',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.5)'
    },
    zoneNumber: {
      position: 'absolute',
      top: '-10px',
      left: '-10px',
      width: '28px',
      height: '28px',
      borderRadius: '50%',
      background: 'linear-gradient(145deg, #00bcd4, #0097a7)',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '0.9rem',
      fontWeight: 'bold',
      border: '2px solid rgba(255, 255, 255, 0.3)',
      boxShadow: '0 0 10px rgba(0, 188, 212, 0.5)'
    },
    techType: {
      position: 'absolute',
      top: '-15px',
      right: '-15px',
      background: 'rgba(0, 0, 0, 0.8)',
      color: '#64b5f6',
      padding: '0.2rem 0.4rem',
      borderRadius: '4px',
      fontSize: '0.6rem',
      fontWeight: 'bold',
      border: '1px solid rgba(100, 181, 246, 0.5)'
    },

    // Modal styles (keeping the techy theme)
    modal: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.8)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      backdropFilter: 'blur(5px)'
    },
    modalContent: {
      background: 'linear-gradient(145deg, rgba(15, 23, 42, 0.95) 0%, rgba(30, 41, 59, 0.95) 100%)',
      borderRadius: '20px',
      padding: '2rem',
      maxWidth: '500px',
      width: '90%',
      color: 'white',
      position: 'relative',
      border: '2px solid rgba(0, 188, 212, 0.5)',
      boxShadow: '0 20px 40px rgba(0, 0, 0, 0.5), 0 0 30px rgba(0, 188, 212, 0.3)'
    },
    modalClose: {
      position: 'absolute',
      top: '1rem',
      right: '1rem',
      background: 'rgba(244, 67, 54, 0.2)',
      border: '1px solid rgba(244, 67, 54, 0.5)',
      borderRadius: '50%',
      width: '40px',
      height: '40px',
      color: '#f44336',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    modalHeader: {
      textAlign: 'center',
      marginBottom: '1.5rem'
    },
    modalEmoji: {
      fontSize: '4rem',
      marginBottom: '1rem'
    },
    modalTitle: {
      fontSize: '1.8rem',
      fontWeight: 'bold',
      marginBottom: '0.5rem',
      color: '#00bcd4',
      textShadow: '0 0 10px rgba(0, 188, 212, 0.5)'
    },
    modalDescription: {
      fontSize: '1rem',
      color: '#b0bec5',
      fontStyle: 'italic'
    },
    modalStats: {
      background: 'rgba(0, 0, 0, 0.3)',
      borderRadius: '12px',
      padding: '1rem',
      marginBottom: '1.5rem',
      border: '1px solid rgba(0, 188, 212, 0.3)'
    },
    modalButtons: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'center'
    },
    modalButton: {
      padding: '1rem 1.5rem',
      border: 'none',
      borderRadius: '10px',
      fontWeight: 'bold',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem'
    },
    playButton: {
      background: 'linear-gradient(145deg, #4caf50, #388e3c)',
      color: 'white',
      border: '1px solid rgba(76, 175, 80, 0.5)',
      boxShadow: '0 4px 15px rgba(76, 175, 80, 0.3)'
    },
    cancelButton: {
      background: 'rgba(120, 113, 108, 0.3)',
      color: 'white',
      border: '1px solid rgba(120, 113, 108, 0.5)'
    }
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <div style={styles.navbar}>
        <div style={styles.userInfo}>
          <Shield size={32} color="#00bcd4" />
          <div>
            <div style={styles.userName}>
              {userData.email.split('@')[0]}'s Cyber Dashboard
            </div>
            <div style={{ fontSize: '0.9rem', opacity: 0.8, color: '#64b5f6' }}>
              Security Level {Math.floor(userData.overall_game_progress / 25) + 1} Analyst
            </div>
          </div>
        </div>
        <div style={styles.navButtons}>
          <button 
            style={styles.navButton}
            onClick={() => navigate('/')}
            onMouseEnter={(e) => e.target.style.background = 'rgba(0, 188, 212, 0.4)'}
            onMouseLeave={(e) => e.target.style.background = 'rgba(0, 188, 212, 0.2)'}
          >
            <Home size={16} />
            Home
          </button>
          <button 
            style={styles.navButton}
            onClick={handleLogout}
            onMouseEnter={(e) => e.target.style.background = 'rgba(244, 67, 54, 0.4)'}
            onMouseLeave={(e) => e.target.style.background = 'rgba(0, 188, 212, 0.2)'}
          >
            <LogOut size={16} />
            Logout
          </button>
        </div>
      </div>

      {/* Cyber Progress Bar */}
      <div style={styles.progressSection}>
        <div style={styles.progressTitle}>
          üõ°Ô∏è Cyber Security Mastery Progress üõ°Ô∏è
        </div>
        <div style={styles.progressBarContainer}>
          <div 
            style={{
              ...styles.progressBar,
              width: `${userData.overall_game_progress}%`
            }}
          />
          <div style={styles.progressText}>
            {userData.overall_game_progress}% Complete
          </div>
        </div>
        <div style={styles.progressMessage}>
          {userData.overall_game_progress < 25 && "üíæ Systems initializing... Building foundation!"}
          {userData.overall_game_progress >= 25 && userData.overall_game_progress < 50 && "üîí Security protocols active! Excellent progress!"}
          {userData.overall_game_progress >= 50 && userData.overall_game_progress < 75 && "‚ö° Advanced systems online! Almost there!"}
          {userData.overall_game_progress >= 75 && userData.overall_game_progress < 100 && "üöÄ Elite level detected! Finalizing mastery!"}
          {userData.overall_game_progress >= 100 && "üëë Cyber Security Expert! All systems secured!"}
        </div>
      </div>

      {/* Dedicated Cyber Security Map Card */}
      <div style={styles.mapCard}>
        <div style={styles.mapTitle}>
          üåê CYBER SECURITY NETWORK MAP üåê
        </div>
        
        <div style={styles.mapContainer}>
          {/* Cyber Network Connection Lines */}
          <svg style={styles.pathSvg}>
            <defs>
              <linearGradient id="cyberPath" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" stopColor="#00bcd4" stopOpacity="0.9" />
                <stop offset="33%" stopColor="#ff9800" stopOpacity="0.9" />
                <stop offset="66%" stopColor="#4caf50" stopOpacity="0.9" />
                <stop offset="100%" stopColor="#9c27b0" stopOpacity="0.9" />
              </linearGradient>
              <filter id="glow">
                <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
                <feMerge> 
                  <feMergeNode in="coloredBlur"/>
                  <feMergeNode in="SourceGraphic"/>
                </feMerge>
              </filter>
            </defs>
            {/* Visible dotted connection path */}
            <path
              d="M 60 100 
                 C 100 150, 150 200, 200 260
                 C 250 300, 350 250, 400 80
                 C 450 120, 480 200, 520 300"
              stroke="url(#cyberPath)"
              strokeWidth="4"
              fill="none"
              strokeDasharray="8,6"
              strokeLinecap="round"
              opacity="0.8"
              filter="url(#glow)"
            />
            {/* Additional connection lines for better visibility */}
            <circle cx="60" cy="100" r="3" fill="#00bcd4" opacity="0.8"/>
            <circle cx="200" cy="260" r="3" fill="#ff9800" opacity="0.8"/>
            <circle cx="400" cy="80" r="3" fill="#4caf50" opacity="0.8"/>
            <circle cx="520" cy="300" r="3" fill="#9c27b0" opacity="0.8"/>
          </svg>

          {/* Security Zones */}
          {zonesWithData.map((zone) => (
            <SecurityZone
              key={zone.gameNum}
              zone={zone}
              onClick={() => handleArenaClick(zone)}
              styles={styles}
            />
          ))}
        </div>
      </div>

      {/* Cyber Modal */}
      {showModal && selectedArena && (
        <div style={styles.modal} onClick={() => setShowModal(false)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <button style={styles.modalClose} onClick={() => setShowModal(false)}>
              <X size={20} />
            </button>
            
            <div style={styles.modalHeader}>
              <div style={styles.modalEmoji}>{selectedArena.emoji}</div>
              <div style={styles.modalTitle}>{selectedArena.name}</div>
              <div style={styles.modalDescription}>{selectedArena.description}</div>
            </div>

            <div style={styles.modalStats}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Security Score:</span>
                <span style={{ fontWeight: 'bold', color: selectedArena.gameStatus.color }}>
                  {selectedArena.score}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>System Status:</span>
                <span style={{ fontWeight: 'bold', color: selectedArena.gameStatus.color }}>
                  {selectedArena.gameStatus.status.toUpperCase()}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span>Completion:</span>
                <span style={{ fontWeight: 'bold' }}>
                  {selectedArena.score > 0 ? `${Math.min(Math.floor(selectedArena.score / 10), 100)}%` : '0%'}
                </span>
              </div>
            </div>

            <div style={styles.modalButtons}>
              <button 
                style={{...styles.modalButton, ...styles.playButton}}
                onClick={handlePlayGame}
                onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
                onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
              >
                <Play size={20} />
                Access System!
              </button>
              <button 
                style={{...styles.modalButton, ...styles.cancelButton}}
                onClick={() => setShowModal(false)}
              >
                Stand By
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// Security Zone Component
function SecurityZone({ zone, onClick, styles }) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      style={{
        ...styles.securityZone,
        ...zone.position,
        background: `linear-gradient(145deg, ${zone.bgColor}, ${zone.color}22)`,
        ...(zone.isLocked ? styles.zoneLocked : {}),
        ...(isHovered && !zone.isLocked ? styles.zoneHover : {})
      }}
      onClick={onClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {zone.isLocked ? 'üîí' : zone.emoji}
      
      {/* Zone Number */}
      <div style={styles.zoneNumber}>
        {zone.gameNum}
      </div>
      
      {/* Tech Type Badge */}
      <div style={styles.techType}>
        {zone.techType}
      </div>
      
      {/* Zone Label */}
      <div style={styles.zoneLabel}>
        {zone.shortName}
      </div>
      
      {/* Status badge */}
      <div style={{
        position: 'absolute',
        top: '-8px',
        right: '-8px',
        width: '24px',
        height: '24px',
        borderRadius: '50%',
        background: zone.gameStatus.color,
        border: '2px solid rgba(255, 255, 255, 0.3)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        boxShadow: `0 0 10px ${zone.gameStatus.color}44`
      }}>
        <zone.gameStatus.icon size={12} color="white" />
      </div>
    </div>
  );
}
