// SnakeGame.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const BOARD_SIZE = 12;
const WIN_SCORE = 12;
const DASHBOARD_ROUTE = "/dashboard";
const TICK_SPEED = 180;  // half as fast as before

function getRandomFood() {
  const x = Math.floor(Math.random() * BOARD_SIZE);
  const y = Math.floor(Math.random() * BOARD_SIZE);
  return { x, y };
}

const getCenter = () => {
  const c = Math.floor(BOARD_SIZE / 2);
  return { x: c, y: c };
};

export default function SnakeGame() {
  const navigate = useNavigate();

  const [snakeHead, setSnakeHead] = useState(getCenter);
  const [direction, setDirection] = useState({ x: 1, y: 0 });
  const [food, setFood] = useState(getRandomFood);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);

  // Win condition ‚Üí return to dashboard
  useEffect(() => {
    if (score >= WIN_SCORE) {
      navigate(DASHBOARD_ROUTE);
    }
  }, [score, navigate]);

  // Movement controls
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (gameOver) return;

      if ((e.key === "ArrowUp" || e.key === "w") && direction.y !== 1)
        setDirection({ x: 0, y: -1 });
      else if ((e.key === "ArrowDown" || e.key === "s") && direction.y !== -1)
        setDirection({ x: 0, y: 1 });
      else if ((e.key === "ArrowLeft" || e.key === "a") && direction.x !== 1)
        setDirection({ x: -1, y: 0 });
      else if ((e.key === "ArrowRight" || e.key === "d") && direction.x !== -1)
        setDirection({ x: 1, y: 0 });
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [direction, gameOver]);

  // Game loop
  useEffect(() => {
    if (gameOver) return;

    const intervalId = setInterval(() => {
      setSnakeHead((prev) => {
        const newHead = { x: prev.x + direction.x, y: prev.y + direction.y };

        // Wall collision
        if (
          newHead.x < 0 ||
          newHead.x >= BOARD_SIZE ||
          newHead.y < 0 ||
          newHead.y >= BOARD_SIZE
        ) {
          setGameOver(true);
          return prev;
        }

        // Food collection
        if (newHead.x === food.x && newHead.y === food.y) {
          setScore((prev) => prev + 1);
          setFood(getRandomFood());
        }

        return newHead;
      });
    }, TICK_SPEED);

    return () => clearInterval(intervalId);
  }, [direction, food, gameOver]);

  const handleRestart = () => {
    setSnakeHead(getCenter());
    setDirection({ x: 1, y: 0 });
    setFood(getRandomFood());
    setGameOver(false);
    setScore(0);
  };

  const handleExit = () => navigate(DASHBOARD_ROUTE);

  // Layout
  const boardStyle = {
    display: "grid",
    gridTemplateColumns: `repeat(${BOARD_SIZE}, 44px)`,
    gridTemplateRows: `repeat(${BOARD_SIZE}, 44px)`,
    background: "#020617",
    padding: "6px",
    border: "4px solid #4b5563",
    borderRadius: "12px",
    boxShadow: "0 0 24px rgba(0,0,0,0.7)",
  };

  const cellStyleBase = {
    width: "44px",
    height: "44px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "36px",   // bigger sprites
    userSelect: "none",
  };

  const cells = [];
  for (let y = 0; y < BOARD_SIZE; y++) {
    for (let x = 0; x < BOARD_SIZE; x++) {
      const isHead = snakeHead.x === x && snakeHead.y === y;
      const isFood = food.x === x && food.y === y;

      cells.push(
        <div key={`${x}-${y}`} style={cellStyleBase}>
          {isHead ? "‚öí" : isFood ? "‚õÅ" : ""}
        </div>
      );
    }
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background:
          "radial-gradient(circle at top, #0f172a 0, #020617 50%, #000 100%)",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        color: "white",
        padding: "1rem",
        gap: "1rem",
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
        position: "relative",
      }}
    >
      {/* Exit button */}
      <button
        onClick={handleExit}
        style={{
          position: "absolute",
          top: "1rem",
          right: "1rem",
          background: "rgba(0,0,0,0.6)",
          borderRadius: "999px",
          border: "1px solid rgba(255,255,255,0.3)",
          color: "white",
          padding: "0.4rem 0.7rem",
          cursor: "pointer",
        }}
      >
        ‚úï Exit
      </button>

      <h1 style={{ fontSize: "2rem" }}>‚öí Cyber Forge Run</h1>
      <p style={{ opacity: 0.8, textAlign: "center" }}>
        Collect <strong>{WIN_SCORE}</strong> ingots to complete your training.
      </p>

      <div style={{ fontSize: "1.15rem", marginBottom: "0.3rem" }}>
        Ingots: <strong>{score}</strong> / {WIN_SCORE}
      </div>

      <div style={boardStyle}>{cells}</div>

      {gameOver && (
        <div
          style={{
            marginTop: "1rem",
            padding: "0.75rem 1.25rem",
            background: "rgba(239, 68, 68, 0.15)",
            border: "1px solid rgba(248, 113, 113, 0.8)",
            borderRadius: "8px",
            textAlign: "center",
          }}
        >
          üíÄ You crashed in the forge!
          <br />
          <button
            onClick={handleRestart}
            style={{
              marginTop: "0.6rem",
              padding: "0.5rem 1rem",
              borderRadius: "999px",
              border: "none",
              background: "linear-gradient(135deg, #22c55e, #16a34a)",
              color: "white",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            Restart
          </button>
        </div>
      )}
    </div>
  );
}
