import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, Star, Trophy, Zap, Play, Lock, Home, LogOut, X, Target, Sword, Eye } from 'lucide-react';

export default function GameDashboard() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedArena, setSelectedArena] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // Get user data from localStorage (set during login)
    const storedUser = localStorage.getItem('cyberslayers_user');
    if (storedUser) {
      setUserData(JSON.parse(storedUser));
    } else {
      // If no user data, redirect to login
      navigate('/login');
    }
    setLoading(false);
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('cyberslayers_user');
    navigate('/');
  };

  const getProgressColor = (progress) => {
    if (progress <= 25) return '#ff6b6b'; // Red
    if (progress <= 50) return '#ffa726'; // Orange  
    if (progress <= 75) return '#ffee58'; // Yellow
    return '#66bb6a'; // Green
  };

  const getGameStatus = (score) => {
    if (score === 0) return { status: 'locked', color: '#94a3b8', icon: Lock };
    if (score <= 300) return { status: 'bronze', color: '#cd7f32', icon: Star };
    if (score <= 600) return { status: 'silver', color: '#c0c0c0', icon: Star };
    if (score <= 800) return { status: 'gold', color: '#ffd700', icon: Trophy };
    return { status: 'diamond', color: '#00bcd4', icon: Zap };
  };

  const handleArenaClick = (arenaData) => {
    if (!arenaData.isLocked) {
      setSelectedArena(arenaData);
      setShowModal(true);
    }
  };

  const handlePlayGame = () => {
    // Navigate to specific game - you'll implement this later
    console.log(`Starting ${selectedArena.name}...`);
    setShowModal(false);
    // navigate(`/game/${selectedArena.gameNum}`);
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'white',
        fontSize: '1.5rem'
      }}>
        Loading your cyber adventure map... üó∫Ô∏è
      </div>
    );
  }

  if (!userData) return null;

  const progressColor = getProgressColor(userData.overall_game_progress);
  const games = userData.games || {};

  // Arena configurations with map positions
  const arenas = [
    {
      gameNum: 1,
      name: "Password Fortress",
      description: "Master the art of creating unbreakable passwords",
      emoji: "üè∞",
      icon: Shield,
      position: { top: '70%', left: '20%' },
      color: '#e91e63',
      terrain: 'mountain'
    },
    {
      gameNum: 2,
      name: "Phishing Bay",
      description: "Learn to spot suspicious emails and links",
      emoji: "üèñÔ∏è", 
      icon: Eye,
      position: { top: '45%', left: '45%' },
      color: '#2196f3',
      terrain: 'water'
    },
    {
      gameNum: 3,
      name: "Malware Jungle",
      description: "Hunt down dangerous software threats",
      emoji: "üå¥",
      icon: Sword,
      position: { top: '25%', left: '70%' },
      color: '#4caf50',
      terrain: 'forest'
    },
    {
      gameNum: 4,
      name: "Network Castle",
      description: "Defend against network intrusions",
      emoji: "üèõÔ∏è",
      icon: Target,
      position: { top: '15%', left: '25%' },
      color: '#9c27b0',
      terrain: 'castle'
    }
  ];

  // Add game data to arenas
  const arenasWithData = arenas.map(arena => {
    const scoreField = arena.gameNum === 3 ? 'game3_scroe' : `game${arena.gameNum}_score`;
    const score = games[scoreField] || 0;
    const gameStatus = getGameStatus(score);
    const isLocked = arena.gameNum > 1 && (
      arena.gameNum === 2 ? games['game1_score'] === 0 : 
      arena.gameNum === 3 ? games['game2_score'] === 0 : 
      arena.gameNum === 4 ? games['game3_scroe'] === 0 : false
    );

    return {
      ...arena,
      score,
      gameStatus,
      isLocked
    };
  });

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #87CEEB 0%, #98FB98 50%, #F0E68C 100%)',
      color: 'white',
      fontFamily: 'Arial, sans-serif',
      position: 'relative',
      overflow: 'hidden'
    },
    header: {
      position: 'absolute',
      top: '1rem',
      left: '1rem',
      right: '1rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      background: 'rgba(255, 255, 255, 0.95)',
      backdropFilter: 'blur(20px)',
      borderRadius: '20px',
      padding: '1rem',
      border: '2px solid rgba(255, 255, 255, 0.3)',
      color: '#333',
      zIndex: 100
    },
    userInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem'
    },
    userName: {
      fontSize: '1.3rem',
      fontWeight: 'bold',
      background: 'linear-gradient(45deg, #667eea, #764ba2)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text'
    },
    navButtons: {
      display: 'flex',
      gap: '0.5rem'
    },
    navButton: {
      padding: '0.5rem 1rem',
      background: 'rgba(103, 126, 234, 0.1)',
      border: '2px solid rgba(103, 126, 234, 0.3)',
      borderRadius: '12px',
      color: '#667eea',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontSize: '0.9rem',
      fontWeight: 'bold'
    },
    progressContainer: {
      position: 'absolute',
      top: '6rem',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'rgba(255, 255, 255, 0.95)',
      backdropFilter: 'blur(20px)',
      borderRadius: '20px',
      padding: '1.5rem 2rem',
      border: '2px solid rgba(255, 255, 255, 0.3)',
      textAlign: 'center',
      color: '#333',
      zIndex: 100,
      minWidth: '300px'
    },
    progressTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '1rem'
    },
    progressBarContainer: {
      width: '100%',
      height: '20px',
      background: 'rgba(0, 0, 0, 0.1)',
      borderRadius: '10px',
      overflow: 'hidden',
      position: 'relative'
    },
    progressBar: {
      height: '100%',
      background: `linear-gradient(90deg, ${progressColor}, ${progressColor}dd)`,
      borderRadius: '10px',
      transition: 'width 1s ease-in-out'
    },
    progressText: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      fontWeight: 'bold',
      fontSize: '0.8rem',
      color: 'white',
      textShadow: '0 1px 2px rgba(0,0,0,0.5)'
    },
    mapContainer: {
      position: 'absolute',
      top: '12rem',
      left: 0,
      right: 0,
      bottom: 0,
      background: `
        radial-gradient(circle at 20% 80%, rgba(233, 30, 99, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 45% 45%, rgba(33, 150, 243, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 70% 25%, rgba(76, 175, 80, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 25% 15%, rgba(156, 39, 176, 0.1) 0%, transparent 50%)
      `,
      position: 'relative'
    },
    pathSvg: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      zIndex: 1
    },
    arena: {
      position: 'absolute',
      width: '120px',
      height: '120px',
      borderRadius: '50%',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      zIndex: 10,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      border: '4px solid white',
      boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
      fontSize: '3rem'
    },
    arenaLocked: {
      filter: 'grayscale(100%)',
      cursor: 'not-allowed',
      opacity: 0.6
    },
    arenaHover: {
      transform: 'scale(1.1)',
      boxShadow: '0 12px 24px rgba(0,0,0,0.3)',
      zIndex: 20
    },
    arenaLabel: {
      position: 'absolute',
      bottom: '-30px',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'rgba(0, 0, 0, 0.8)',
      color: 'white',
      padding: '0.3rem 0.8rem',
      borderRadius: '12px',
      fontSize: '0.8rem',
      fontWeight: 'bold',
      whiteSpace: 'nowrap'
    },
    modal: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.7)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    },
    modalContent: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      borderRadius: '24px',
      padding: '2rem',
      maxWidth: '500px',
      width: '90%',
      color: 'white',
      position: 'relative',
      border: '3px solid rgba(255, 255, 255, 0.3)'
    },
    modalClose: {
      position: 'absolute',
      top: '1rem',
      right: '1rem',
      background: 'rgba(255, 255, 255, 0.2)',
      border: 'none',
      borderRadius: '50%',
      width: '40px',
      height: '40px',
      color: 'white',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    modalHeader: {
      textAlign: 'center',
      marginBottom: '1.5rem'
    },
    modalEmoji: {
      fontSize: '4rem',
      marginBottom: '1rem'
    },
    modalTitle: {
      fontSize: '2rem',
      fontWeight: 'bold',
      marginBottom: '0.5rem'
    },
    modalDescription: {
      fontSize: '1.1rem',
      opacity: 0.9
    },
    modalStats: {
      background: 'rgba(255, 255, 255, 0.1)',
      borderRadius: '16px',
      padding: '1rem',
      marginBottom: '1.5rem'
    },
    modalButtons: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'center'
    },
    modalButton: {
      padding: '1rem 2rem',
      border: 'none',
      borderRadius: '12px',
      fontWeight: 'bold',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem'
    },
    playButton: {
      background: 'linear-gradient(45deg, #00bcd4, #2196f3)',
      color: 'white'
    },
    cancelButton: {
      background: 'rgba(255, 255, 255, 0.2)',
      color: 'white'
    }
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.userInfo}>
          <Shield size={32} color="#667eea" />
          <div>
            <div style={styles.userName}>
              {userData.email.split('@')[0]}'s Adventure
            </div>
            <div style={{ fontSize: '0.9rem', opacity: 0.7 }}>
              Level {Math.floor(userData.overall_game_progress / 25) + 1} Cyber Explorer
            </div>
          </div>
        </div>
        <div style={styles.navButtons}>
          <button 
            style={styles.navButton}
            onClick={() => navigate('/')}
            onMouseEnter={(e) => e.target.style.background = 'rgba(103, 126, 234, 0.2)'}
            onMouseLeave={(e) => e.target.style.background = 'rgba(103, 126, 234, 0.1)'}
          >
            <Home size={16} />
            Home
          </button>
          <button 
            style={styles.navButton}
            onClick={handleLogout}
            onMouseEnter={(e) => e.target.style.background = 'rgba(255, 69, 58, 0.2)'}
            onMouseLeave={(e) => e.target.style.background = 'rgba(103, 126, 234, 0.1)'}
          >
            <LogOut size={16} />
            Logout
          </button>
        </div>
      </div>

      {/* Progress Section */}
      <div style={styles.progressContainer}>
        <div style={styles.progressTitle}>
          üó∫Ô∏è Adventure Progress üó∫Ô∏è
        </div>
        <div style={styles.progressBarContainer}>
          <div 
            style={{
              ...styles.progressBar,
              width: `${userData.overall_game_progress}%`
            }}
          />
          <div style={styles.progressText}>
            {userData.overall_game_progress}%
          </div>
        </div>
      </div>

      {/* Game Map */}
      <div style={styles.mapContainer}>
        {/* Path connecting arenas */}
        <svg style={styles.pathSvg}>
          <defs>
            <linearGradient id="pathGradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#ffd700" stopOpacity="0.8" />
              <stop offset="50%" stopColor="#ff6b6b" stopOpacity="0.8" />
              <stop offset="100%" stopColor="#4ecdc4" stopOpacity="0.8" />
            </linearGradient>
          </defs>
          <path
            d={`M ${window.innerWidth * 0.2} ${(window.innerHeight - 192) * 0.7} 
                Q ${window.innerWidth * 0.35} ${(window.innerHeight - 192) * 0.5} 
                ${window.innerWidth * 0.45} ${(window.innerHeight - 192) * 0.45}
                Q ${window.innerWidth * 0.6} ${(window.innerHeight - 192) * 0.35} 
                ${window.innerWidth * 0.7} ${(window.innerHeight - 192) * 0.25}
                Q ${window.innerWidth * 0.5} ${(window.innerHeight - 192) * 0.15} 
                ${window.innerWidth * 0.25} ${(window.innerHeight - 192) * 0.15}`}
            stroke="url(#pathGradient)"
            strokeWidth="8"
            fill="none"
            strokeDasharray="20,10"
            strokeLinecap="round"
            opacity="0.7"
          />
        </svg>

        {/* Arena locations */}
        {arenasWithData.map((arena) => (
          <ArenaLocation
            key={arena.gameNum}
            arena={arena}
            onClick={() => handleArenaClick(arena)}
            styles={styles}
          />
        ))}
      </div>

      {/* Game Modal */}
      {showModal && selectedArena && (
        <div style={styles.modal} onClick={() => setShowModal(false)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <button 
              style={styles.modalClose}
              onClick={() => setShowModal(false)}
            >
              <X size={20} />
            </button>
            
            <div style={styles.modalHeader}>
              <div style={styles.modalEmoji}>{selectedArena.emoji}</div>
              <div style={styles.modalTitle}>{selectedArena.name}</div>
              <div style={styles.modalDescription}>{selectedArena.description}</div>
            </div>

            <div style={styles.modalStats}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Current Score:</span>
                <span style={{ fontWeight: 'bold' }}>{selectedArena.score}</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Status:</span>
                <span style={{ fontWeight: 'bold', color: selectedArena.gameStatus.color }}>
                  {selectedArena.gameStatus.status.toUpperCase()}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span>Progress:</span>
                <span style={{ fontWeight: 'bold' }}>
                  {selectedArena.score > 0 ? `${Math.min(Math.floor(selectedArena.score / 10), 100)}%` : '0%'}
                </span>
              </div>
            </div>

            <div style={styles.modalButtons}>
              <button 
                style={{...styles.modalButton, ...styles.playButton}}
                onClick={handlePlayGame}
                onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
                onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
              >
                <Play size={20} />
                Start Adventure!
              </button>
              <button 
                style={{...styles.modalButton, ...styles.cancelButton}}
                onClick={() => setShowModal(false)}
              >
                Not Yet
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// Arena Location Component
function ArenaLocation({ arena, onClick, styles }) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      style={{
        ...styles.arena,
        ...arena.position,
        background: `linear-gradient(135deg, ${arena.color}, ${arena.color}dd)`,
        ...(arena.isLocked ? styles.arenaLocked : {}),
        ...(isHovered && !arena.isLocked ? styles.arenaHover : {})
      }}
      onClick={onClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {arena.isLocked ? 'üîí' : arena.emoji}
      <div style={styles.arenaLabel}>
        {arena.name}
      </div>
      
      {/* Status indicator */}
      <div style={{
        position: 'absolute',
        top: '-10px',
        right: '-10px',
        width: '30px',
        height: '30px',
        borderRadius: '50%',
        background: arena.gameStatus.color,
        border: '2px solid white',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <arena.gameStatus.icon size={16} color="white" />
      </div>
    </div>
  );
}
