    minHeight: '100vh',
    background:
      'radial-gradient(circle at top, #020617 0, #020617 40%, #000 100%)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    fontFamily:
      '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    padding: '1rem',
    gap: '1rem',
    position: 'relative'
  };

  const gameAreaStyle = {
    position: 'relative',
    width: GAME_WIDTH,
    height: GAME_HEIGHT,
    background:
      'linear-gradient(180deg, #020617 0%, #020617 60%, #000 100%)',
    border: '4px solid #4b5563',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 0 25px rgba(0,0,0,0.8)'
  };

  const hudStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    width: GAME_WIDTH,
    fontSize: '0.9rem',
    opacity: 0.9
  };

  return (
    <div style={containerStyle}>
      {/* Exit */}
      <button
        onClick={handleExit}
        style={{
          position: 'absolute',
          top: '1rem',
          right: '1rem',
          background: 'rgba(0,0,0,0.6)',
          borderRadius: '999px',
          border: '1px solid rgba(255,255,255,0.3)',
          color: 'white',
          padding: '0.4rem 0.7rem',
          cursor: 'pointer',
          fontSize: '0.85rem'
        }}
      >
        ‚úï Exit
      </button>

      <h1 style={{ fontSize: '2rem', textAlign: 'center' }}>
        üöÄ Starfield Defender
      </h1>
      <p style={{ opacity: 0.8, textAlign: 'center', maxWidth: 500 }}>
        Move with <strong>‚Üê ‚Üí / A D</strong>, shoot with{' '}
        <strong>Space / ‚Üë / W</strong>. Clear all invaders before they reach you.
      </p>

      <div style={hudStyle}>
        <div>Score: {score}</div>
        <div>
          Status:{' '}
          {gameState === 'playing'
            ? 'ENGAGED'
            : gameState === 'win'
            ? 'VICTORY'
            : 'CRITICAL FAILURE'}
        </div>
      </div>

      <div style={gameAreaStyle}>
        {/* Player */}
        <div
          style={{
            position: 'absolute',
            width: PLAYER_WIDTH,
            height: PLAYER_HEIGHT,
            left: playerX,
            bottom: 20,
            background:
              'linear-gradient(135deg, #22c55e, #16a34a)',
            borderRadius: '6px 6px 2px 2px',
            boxShadow: '0 0 15px rgba(34,197,94,0.8)',
            border: '2px solid #22c55e',
            transform: 'translateY(0)'
          }}
        >
          {/* little cockpit triangle */}
          <div
            style={{
              width: 0,
              height: 0,
              borderLeft: `${PLAYER_WIDTH / 2}px solid transparent`,
              borderRight: `${PLAYER_WIDTH / 2}px solid transparent`,
              borderBottom: '10px solid #bbf7d0',
              position: 'absolute',
              top: -10,
              left: 0
            }}
          />
        </div>

        {/* Bullets */}
        {bullets.map((b, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              width: BULLET_WIDTH,
              height: BULLET_HEIGHT,
              left: b.x,
              top: b.y,
              background:
                'linear-gradient(180deg,#facc15,#f97316)',
              boxShadow: '0 0 8px rgba(250,204,21,0.9)',
              borderRadius: '999px'
            }}
          />
        ))}

        {/* Enemies */}
        {enemies.map(
          (e, i) =>
            e.alive && (
              <div
                key={i}
                style={{
                  position: 'absolute',
                  width: ENEMY_WIDTH,
                  height: ENEMY_HEIGHT,
                  left: e.x,
                  top: e.y + enemyOffsetY,
                  background: 'linear-gradient(135deg,#f97316,#db2777)',
                  borderRadius: '4px',
                  border: '2px solid #fb7185',
                  boxShadow: '0 0 10px rgba(251,113,133,0.8)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '0.7rem'
                }}
              >
                üëæ
              </div>
            )
        )}

        {/* End-of-game overlay */}
        {gameState !== 'playing' && (
          <div
            style={{
              position: 'absolute',
              inset: 0,
              background: 'rgba(0,0,0,0.75)',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '0.75rem',
              textAlign: 'center',
              padding: '1rem'
            }}
          >
            <h2 style={{ fontSize: '1.6rem', marginBottom: '0.25rem' }}>
              {gameState === 'win' ? 'VICTORY!' : 'MISSION FAILED'}
            </h2>
            <p style={{ opacity: 0.8 }}>
              {gameState === 'win'
                ? 'You cleared the wave of invaders. The network is safe... for now.'
                : 'The invaders breached your defenses. Recalibrate and try again.'}
            </p>
            <div style={{ display: 'flex', gap: '0.5rem', marginTop: '0.5rem' }}>
              <button
                onClick={handleRestart}
                style={{
                  padding: '0.6rem 1.1rem',
                  borderRadius: '999px',
                  border: 'none',
                  background:
                    'linear-gradient(135deg,#22c55e,#16a34a)',
                  color: 'white',
                  fontWeight: 'bold',
                  cursor: 'pointer'
                }}
              >
                Play Again
              </button>
              <button
                onClick={handleExit}
                style={{
                  padding: '0.6rem 1.1rem',
                  borderRadius: '999px',
                  border: '1px solid rgba(148,163,184,0.6)',
                  background: 'rgba(15,23,42,0.9)',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                Back to Dashboard
              </button>
            </div>
          </div>
        )}
      </div>

      {gameState === 'playing' && (
        <button
          onClick={handleRestart}
          style={{
            marginTop: '0.4rem',
            padding: '0.35rem 0.9rem',
            borderRadius: '999px',
            border: '1px solid rgba(148,163,184,0.6)',
            background: 'rgba(15,23,42,0.7)',
            color: 'white',
            fontSize: '0.85rem',
            cursor: 'pointer'
          }}
        >
          Reset Wave
        </button>
      )}
    </div>
  );
}

function createInitialEnemies() {
  const enemies = [];
  const startX = (GAME_WIDTH - (ENEMY_COLS - 1) * ENEMY_X_SPACING) / 2;

  for (let row = 0; row < ENEMY_ROWS; row++) {
    for (let col = 0; col < ENEMY_COLS; col++) {
      enemies.push({
        x: startX + col * ENEMY_X_SPACING,
        y: ENEMY_START_Y + row * ENEMY_Y_SPACING,
        alive: true
      });
    }
  }
  return enemies;
}
