                      setShowModal(false);
                      setShowLearningModule1(true);
                    } else {
                      // Games 2â€“4: inline quiz flow
                      setShowLesson(false);
                      setShowQuiz(true);
                    }
                  }}
                >
                  {selectedArena.gameNum === 1
                    ? 'Begin Training'
                    : 'Proceed to Quiz'}
