        },
        {
          question: 'How does guarding your online presence protect your future?',
          options: [
            'It makes you more popular on social media',
            'Because posts can be used later for reputation, privacy, or security attacks',
            'It helps you get more followers',
            'It makes your computer run faster'
          ],
          correctAnswer: 1
        }
      ]
    }
  ]
};

function LearningModule({ onEnterGame, onClose }) {
  const [currentModule] = useState(modulesData.modules[0]);
  const [stage, setStage] = useState('scroll');
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState({});
  const [score, setScore] = useState(0);
  const [showExplanation, setShowExplanation] = useState(false);

  const handleScrollComplete = () => {
    setStage('quiz');
  };

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    setSelectedAnswers({
      ...selectedAnswers,
      [questionIndex]: answerIndex
    });
  };

  const handleNextQuestion = () => {
    if (currentQuestion < currentModule.quiz.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setShowExplanation(false);
    } else {
      calculateScore();
    }
  };

  const calculateScore = () => {
    let correct = 0;
    currentModule.quiz.forEach((q, index) => {
      if (selectedAnswers[index] === q.correctAnswer) {
        correct++;
      }
    });
    const percentage = (correct / currentModule.quiz.length) * 100;
    setScore(percentage);
    setStage('result');
  };

  const handleRetakeQuiz = () => {
    setCurrentQuestion(0);
    setSelectedAnswers({});
    setScore(0);
    setShowExplanation(false);
    setStage('quiz');
  };

  const currentQ = currentModule.quiz[currentQuestion];
  const isPassed = score >= 75;

  return (
    <div
      style={{
        position: 'fixed',
        inset: 0,
        zIndex: 9999,
        minHeight: '100vh',
        background:
          'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '2rem',
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
      }}
    >
      {/* Close button */}
      <button
        onClick={onClose}
        style={{
          position: 'absolute',
          top: '1.5rem',
          right: '1.5rem',
          background: 'rgba(0,0,0,0.6)',
          borderRadius: '999px',
          border: '1px solid rgba(255,255,255,0.3)',
          color: 'white',
          padding: '0.4rem 0.7rem',
          display: 'flex',
          alignItems: 'center',
          gap: '0.25rem',
          cursor: 'pointer'
        }}
      >
        <X size={16} /> Exit
      </button>

      {stage === 'scroll' && (
        <div
          style={{
            position: 'relative',
            maxWidth: '800px',
            width: '100%'
          }}
        >
          <div
            style={{
              position: 'relative',
              background: 'linear-gradient(135deg, #d4a574, #e8c99a, #d4a574)',
              borderRadius: '20px',
              padding: '3rem 2rem',
              boxShadow: '0 20px 60px rgba(0,0,0,0.5)',
              border: '10px solid #4a4a4a',
              borderTop: '20px solid #4a4a4a',
              borderBottom: '20px solid #4a4a4a'
            }}
          >
            <div
              style={{
                position: 'absolute',
                left: '20px',
                top: '30px',
                bottom: '30px',
                width: '15px',
                background: 'linear-gradient(180deg, #00bcd4, #0a5f6f)',
                opacity: 0.6
              }}
            />
            <div
              style={{
                position: 'absolute',
                right: '20px',
                top: '30px',
                bottom: '30px',
                width: '15px',
                background: 'linear-gradient(180deg, #00bcd4, #0a5f6f)',
                opacity: 0.6
              }}
            />

            <div
              style={{
                color: '#2c1810',
                fontSize: '1.1rem',
                lineHeight: '1.8',
                fontFamily: 'Georgia, serif',
                whiteSpace: 'pre-line',
                textAlign: 'justify',
                maxHeight: '500px',
                overflowY: 'auto',
                paddingLeft: '30px',
                paddingRight: '30px'
              }}
            >
              <h2
                style={{
                  textAlign: 'center',
                  marginBottom: '1.5rem',
                  fontSize: '1.8rem',
                  color: '#1a0f0a',
                  fontWeight: 'bold',
                  textDecoration: 'underline',
                  textDecorationColor: '#00bcd4'
                }}
              >
                {currentModule.title}
              </h2>
              {currentModule.content}
            </div>
          </div>

          <div
            style={{
              display: 'flex',
              justifyContent: 'center',
              marginTop: '2rem'
            }}
          >
            <button
              onClick={handleScrollComplete}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                padding: '1rem 2rem',
                background: 'linear-gradient(135deg, #00bcd4, #e91e63)',
                border: 'none',
                borderRadius: '12px',
                color: 'white',
                fontSize: '1.2rem',
                fontWeight: 'bold',
                cursor: 'pointer',
                boxShadow: '0 8px 20px rgba(0,188,212,0.4)',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.05)';
                e.currentTarget.style.boxShadow =
                  '0 12px 30px rgba(0,188,212,0.6)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow =
                  '0 8px 20px rgba(0,188,212,0.4)';
              }}
            >
              Continue to Quiz <ArrowRight size={24} />
            </button>
          </div>
        </div>
      )}

      {stage === 'quiz' && (
        <div
          style={{
            maxWidth: '700px',
            width: '100%',
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(20px)',
            borderRadius: '20px',
            padding: '2rem',
            border: '2px solid rgba(0, 188, 212, 0.5)',
            boxShadow: '0 20px 60px rgba(0,0,0,0.5)'
          }}
        >
          <div
            style={{
              marginBottom: '2rem',
              color: 'white'
            }}
          >
            <div
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                marginBottom: '0.5rem',
                fontSize: '0.9rem',
                opacity: 0.8
              }}
            >
              <span>
                Question {currentQuestion + 1} of {currentModule.quiz.length}
              </span>
              <span>{Object.keys(selectedAnswers).length} answered</span>
            </div>
            <div
              style={{
                height: '8px',
                background: 'rgba(255,255,255,0.2)',
                borderRadius: '4px',
                overflow: 'hidden'
              }}
            >
              <div
                style={{
                  height: '100%',
                  background: 'linear-gradient(90deg, #00bcd4, #e91e63)',
                  width: `${
                    ((currentQuestion + 1) / currentModule.quiz.length) * 100
                  }%`,
                  transition: 'width 0.3s ease'
                }}
              />
            </div>
          </div>

          <h3
            style={{
              color: 'white',
              fontSize: '1.4rem',
              marginBottom: '2rem',
              lineHeight: '1.6'
            }}
          >
            {currentQ.question}
          </h3>

          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              gap: '1rem',
              marginBottom: '2rem'
            }}
          >
            {currentQ.options.map((option, index) => {
              const isSelected = selectedAnswers[currentQuestion] === index;
              const isCorrect = index === currentQ.correctAnswer;
              const showResult = showExplanation;

              return (
                <button
                  key={index}
                  onClick={() => {
                    handleAnswerSelect(currentQuestion, index);
                    setShowExplanation(true);
                  }}
                  disabled={showExplanation}
                  style={{
                    padding: '1.2rem',
                    background: showResult
                      ? isCorrect
                        ? 'rgba(76, 175, 80, 0.3)'
                        : isSelected
                        ? 'rgba(244, 67, 54, 0.3)'
                        : 'rgba(255,255,255,0.1)'
                      : isSelected
                      ? 'rgba(0, 188, 212, 0.3)'
                      : 'rgba(255,255,255,0.1)',
                    border: showResult
                      ? isCorrect
                        ? '2px solid #4caf50'
                        : isSelected
                        ? '2px solid #f44336'
                        : '2px solid rgba(255,255,255,0.2)'
                      : isSelected
                      ? '2px solid #00bcd4'
                      : '2px solid rgba(255,255,255,0.2)',
                    borderRadius: '12px',
                    color: 'white',
                    fontSize: '1rem',
                    cursor: showExplanation ? 'default' : 'pointer',
                    textAlign: 'left',
                    transition: 'all 0.3s ease',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '1rem'
                  }}
                >
                  <div
                    style={{
                      width: '30px',
                      height: '30px',
                      borderRadius: '50%',
                      background:
                        showResult && isCorrect
                          ? '#4caf50'
                          : 'rgba(255,255,255,0.2)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      flexShrink: 0
                    }}
                  >
                    {showResult && isCorrect && <Check size={20} />}
                    {!showResult && String.fromCharCode(65 + index)}
                  </div>
                  {option}
                </button>
              );
            })}
          </div>

          {showExplanation && (
            <div
              style={{
                padding: '1rem',
                background:
                  selectedAnswers[currentQuestion] === currentQ.correctAnswer
                    ? 'rgba(76, 175, 80, 0.2)'
                    : 'rgba(244, 67, 54, 0.2)',
                borderRadius: '12px',
                marginBottom: '1.5rem',
                display: 'flex',
                alignItems: 'center',
                gap: '0.8rem'
              }}
            >
              {selectedAnswers[currentQuestion] === currentQ.correctAnswer ? (
                <>
                  <Check size={24} color="#4caf50" />
                  <span style={{ color: 'white' }}>
                    Correct! Well done, cyber warrior! ðŸ›¡ï¸
                  </span>
                </>
              ) : (
                <>
                  <AlertCircle size={24} color="#f44336" />
                  <span style={{ color: 'white' }}>
                    Not quite. The correct answer is highlighted above.
                  </span>
                </>
              )}
            </div>
          )}

          {showExplanation && (
            <button
              onClick={handleNextQuestion}
              style={{
                width: '100%',
                padding: '1rem',
                background: 'linear-gradient(135deg, #00bcd4, #e91e63)',
                border: 'none',
                borderRadius: '12px',
                color: 'white',
                fontSize: '1.1rem',
                fontWeight: 'bold',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '0.5rem',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.02)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
              }}
            >
              {currentQuestion < currentModule.quiz.length - 1
                ? 'Next Question'
                : 'See Results'}
              <ArrowRight size={20} />
            </button>
          )}
        </div>
      )}

      {stage === 'result' && (
        <div
          style={{
            maxWidth: '600px',
            width: '100%',
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(20px)',
            borderRadius: '20px',
            padding: '3rem',
            border: '2px solid rgba(0, 188, 212, 0.5)',
            boxShadow: '0 20px 60px rgba(0,0,0,0.5)',
            textAlign: 'center'
          }}
        >
          <div
            style={{
              width: '200px',
              height: '200px',
              margin: '0 auto 2rem',
              borderRadius: '50%',
              background: `conic-gradient(${
                isPassed ? '#4caf50' : '#f44336'
              } ${score}%, rgba(255,255,255,0.2) ${score}%)`,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              position: 'relative'
            }}
          >
            <div
              style={{
                width: '170px',
                height: '170px',
                borderRadius: '50%',
                background: 'linear-gradient(135deg, #1a1a2e, #16213e)',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white'
              }}
            >
              <div style={{ fontSize: '3rem', fontWeight: 'bold' }}>
                {Math.round(score)}%
              </div>
              <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>
                Your Score
              </div>
            </div>
          </div>

          <h2
            style={{
              color: 'white',
              fontSize: '2rem',
              marginBottom: '1rem'
            }}
          >
            {isPassed ? 'ðŸŽ‰ Quest Complete! ðŸŽ‰' : 'ðŸ’ª Almost There! ðŸ’ª'}
          </h2>

          <p
            style={{
              color: 'white',
              fontSize: '1.1rem',
              marginBottom: '2rem',
              opacity: 0.9
            }}
          >
            {isPassed
              ? 'You have proven your knowledge! The path to the game is now open, brave warrior.'
              : 'You need 75% or higher to unlock the game. Study the teachings and try again!'}
          </p>

          <div
            style={{
              display: 'grid',
              gridTemplateColumns: '1fr 1fr',
              gap: '1rem',
              marginBottom: '2rem'
            }}
          >
            <div
              style={{
                background: 'rgba(255,255,255,0.1)',
                padding: '1rem',
                borderRadius: '12px'
              }}
            >
              <div
                style={{
                  color: '#00bcd4',
                  fontSize: '2rem',
                  fontWeight: 'bold'
                }}
              >
                {
                  Object.values(selectedAnswers).filter(
                    (ans, i) =>
                      ans === currentModule.quiz[i].correctAnswer
                  ).length
                }
              </div>
              <div style={{ color: 'white', fontSize: '0.9rem' }}>
                Correct
              </div>
            </div>
            <div
              style={{
                background: 'rgba(255,255,255,0.1)',
                padding: '1rem',
                borderRadius: '12px'
              }}
            >
              <div
                style={{
                  color: '#e91e63',
                  fontSize: '2rem',
                  fontWeight: 'bold'
                }}
              >
                {currentModule.quiz.length -
                  Object.values(selectedAnswers).filter(
                    (ans, i) =>
                      ans === currentModule.quiz[i].correctAnswer
                  ).length}
              </div>
              <div style={{ color: 'white', fontSize: '0.9rem' }}>
                Incorrect
              </div>
            </div>
          </div>

          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              gap: '1rem'
            }}
          >
            {isPassed ? (
              <button
                onClick={onEnterGame}
                style={{
                  padding: '1.2rem',
                  background: 'linear-gradient(135deg, #4caf50, #8bc34a)',
                  border: 'none',
                  borderRadius: '12px',
                  color: 'white',
                  fontSize: '1.2rem',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '0.5rem',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'scale(1.05)';
                  e.currentTarget.style.boxShadow =
                    '0 12px 30px rgba(76,175,80,0.4)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'scale(1)';
                  e.currentTarget.style.boxShadow = 'none';
                }}
              >
                <Trophy size={24} />
                Enter the Game Arena!
              </button>
            ) : (
              <button
                onClick={handleRetakeQuiz}
                style={{
                  padding: '1.2rem',
                  background: 'linear-gradient(135deg, #00bcd4, #e91e63)',
                  border: 'none',
                  borderRadius: '12px',
                  color: 'white',
                  fontSize: '1.2rem',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'scale(1)';
                }}
              >
                Try Again
              </button>
            )}

            <button
              onClick={() => setStage('scroll')}
              style={{
                padding: '1rem',
                background: 'rgba(255,255,255,0.1)',
                border: '2px solid rgba(255,255,255,0.3)',
                borderRadius: '12px',
                color: 'white',
                fontSize: '1rem',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '0.5rem'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.background = 'rgba(255,255,255,0.2)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.background = 'rgba(255,255,255,0.1)';
              }}
            >
              <BookOpen size={20} />
              Review Scroll Again
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
