// SnakeGame.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import hammerAnvil from '../assets/hammer-anvil.png'; // <-- adjust path/name if needed
import ingot from '../assets/ingot.png';              // <-- adjust path/name if needed

const BOARD_SIZE = 12;        // smaller grid
const WIN_SCORE = 12;         // only need 12 apples/ingots
const DASHBOARD_ROUTE = '/dashboard';
const TICK_SPEED = 90;        // faster movement (ms per tick)

function getRandomFood() {
  const x = Math.floor(Math.random() * BOARD_SIZE);
  const y = Math.floor(Math.random() * BOARD_SIZE);
  return { x, y };
}

const getCenter = () => {
  const c = Math.floor(BOARD_SIZE / 2);
  return { x: c, y: c };
};

export default function SnakeGame() {
  const navigate = useNavigate();

  // Snake is just a single â€œavatarâ€ now, not a growing body
  const [snakeHead, setSnakeHead] = useState(getCenter);
  const [direction, setDirection] = useState({ x: 1, y: 0 });
  const [food, setFood] = useState(getRandomFood);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);

  // === WIN CONDITION: 12 ingots collected => go back to dashboard ===
  useEffect(() => {
    if (score >= WIN_SCORE) {
      navigate(DASHBOARD_ROUTE);
    }
  }, [score, navigate]);

  // keyboard controls
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (gameOver) return;

      if (e.key === 'ArrowUp' || e.key === 'w') {
        if (direction.y === 1) return;
        setDirection({ x: 0, y: -1 });
      } else if (e.key === 'ArrowDown' || e.key === 's') {
        if (direction.y === -1) return;
        setDirection({ x: 0, y: 1 });
      } else if (e.key === 'ArrowLeft' || e.key === 'a') {
        if (direction.x === 1) return;
        setDirection({ x: -1, y: 0 });
      } else if (e.key === 'ArrowRight' || e.key === 'd') {
        if (direction.x === -1) return;
        setDirection({ x: 1, y: 0 });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [direction, gameOver]);

  // game loop
  useEffect(() => {
    if (gameOver) return;

    const intervalId = setInterval(() => {
      setSnakeHead((prevHead) => {
        const newHead = {
          x: prevHead.x + direction.x,
          y: prevHead.y + direction.y
        };

        // wall collision
        if (
          newHead.x < 0 ||
          newHead.x >= BOARD_SIZE ||
          newHead.y < 0 ||
          newHead.y >= BOARD_SIZE
        ) {
          setGameOver(true);
          return prevHead;
        }

        // food collision (collect ingot)
        if (newHead.x === food.x && newHead.y === food.y) {
          // increment by EXACTLY 1
          setScore((prev) => prev + 1);
          setFood(getRandomFood());
        }

        return newHead;
      });
    }, TICK_SPEED);

    return () => clearInterval(intervalId);
  }, [direction, food, gameOver]);

  const handleRestart = () => {
    const center = getCenter();
    setSnakeHead(center);
    setDirection({ x: 1, y: 0 });
    setFood(getRandomFood());
    setGameOver(false);
    setScore(0);
  };

  const handleExit = () => {
    navigate(DASHBOARD_ROUTE);
  };

  const boardStyle = {
    display: 'grid',
    gridTemplateColumns: `repeat(${BOARD_SIZE}, 28px)`,
    gridTemplateRows: `repeat(${BOARD_SIZE}, 28px)`,
    background: '#020617',
    border: '4px solid #4b5563',
    borderRadius: '12px',
    boxShadow: '0 0 24px rgba(0,0,0,0.7)',
    padding: '4px',
  };

  const cellStyleBase = {
    width: '28px',
    height: '28px',
    boxSizing: 'border-box',
    // No per-cell border => no visible grid outlines
    background: '#020617',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const cells = [];
  for (let y = 0; y < BOARD_SIZE; y++) {
    for (let x = 0; x < BOARD_SIZE; x++) {
      const key = `${x}-${y}`;
      const isHead = snakeHead.x === x && snakeHead.y === y;
      const isFood = food.x === x && food.y === y;

      let content = null;

      if (isHead) {
        content = (
          <img
            src={hammerAnvil}
            alt="hammer and anvil"
            style={{
              width: '80%',
              height: '80%',
              objectFit: 'contain',
              pointerEvents: 'none',
              transition: 'transform 0.08s linear',
            }}
          />
        );
      } else if (isFood) {
        content = (
          <img
            src={ingot}
            alt="ingot"
            style={{
              width: '75%',
              height: '75%',
              objectFit: 'contain',
              pointerEvents: 'none',
            }}
          />
        );
      }

      cells.push(
        <div
          key={key}
          style={{
            ...cellStyleBase,
          }}
        >
          {content}
        </div>
      );
    }
  }

  return (
    <div
      style={{
        minHeight: '100vh',
        background:
          'radial-gradient(circle at top, #0f172a 0, #020617 50%, #000 100%)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'white',
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
        padding: '1rem',
        gap: '1rem',
        position: 'relative'
      }}
    >
      {/* Exit (X) button to go back to dashboard */}
      <button
        onClick={handleExit}
        style={{
          position: 'absolute',
          top: '1rem',
          right: '1rem',
          background: 'rgba(0,0,0,0.6)',
          borderRadius: '999px',
          border: '1px solid rgba(255,255,255,0.3)',
          color: 'white',
          padding: '0.4rem 0.7rem',
          cursor: 'pointer',
          fontSize: '0.85rem'
        }}
      >
        âœ• Exit
      </button>

      <h1 style={{ fontSize: '2rem', marginBottom: '0.25rem' }}>
        âš’ Cyber Forge Run
      </h1>
      <p style={{ opacity: 0.8, marginBottom: '0.5rem', textAlign: 'center' }}>
        Guide the <strong>hammer & anvil</strong> through the forge and collect{' '}
        <strong>{WIN_SCORE}</strong> ingots.<br />
        Use <strong>Arrow keys</strong> or <strong>W/A/S/D</strong> to move.
      </p>

      <div style={{ marginBottom: '0.5rem', fontSize: '1.1rem' }}>
        Ingots Collected: <strong>{score}</strong> / {WIN_SCORE}
      </div>

      <div style={boardStyle}>{cells}</div>

      {gameOver && score < WIN_SCORE && (
        <div
          style={{
            marginTop: '1rem',
            padding: '0.75rem 1.25rem',
            background: 'rgba(239, 68, 68, 0.1)',
            border: '1px solid rgba(248, 113, 113, 0.8)',
            borderRadius: '8px',
            textAlign: 'center'
          }}
        >
          <div style={{ marginBottom: '0.5rem' }}>ðŸ’€ You crashed in the forge!</div>
          <button
            onClick={handleRestart}
            style={{
              padding: '0.5rem 1rem',
              borderRadius: '999px',
              border: 'none',
              background: 'linear-gradient(135deg, #22c55e, #16a34a)',
              color: 'white',
              fontWeight: 'bold',
              cursor: 'pointer'
            }}
          >
            Restart
          </button>
        </div>
      )}

      {!gameOver && score < WIN_SCORE && (
        <button
          onClick={handleRestart}
          style={{
            marginTop: '0.5rem',
            padding: '0.35rem 0.9rem',
            borderRadius: '999px',
            border: '1px solid rgba(148,163,184,0.6)',
            background: 'rgba(15,23,42,0.7)',
            color: 'white',
            fontSize: '0.85rem',
            cursor: 'pointer'
          }}
        >
          Reset Game
        </button>
      )}
    </div>
  );
}
