// GalagaGame.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const DASHBOARD_ROUTE = '/dashboard';

const GAME_WIDTH = 720;
const GAME_HEIGHT = 900;

const PLAYER_WIDTH = 50;
const PLAYER_HEIGHT = 40;
const PLAYER_SPEED = 6;

const BULLET_WIDTH = 6;
const BULLET_HEIGHT = 16;
const BULLET_SPEED = 10;
const ENEMY_BULLET_SPEED = 5;

const ENEMY_WIDTH = 44;
const ENEMY_HEIGHT = 36;

const TICK_MS = 16;
const PLAYER_LIVES = 5;

// Enemy types with different behaviors
const ENEMY_TYPES = {
  MALWARE: {
    name: 'ü¶†',
    color: 'linear-gradient(135deg, #dc2626, #991b1b)',
    border: '#ef4444',
    points: 100,
    speed: 2,
    canShoot: false,
    movement: 'zigzag'
  },
  PHISHING: {
    name: 'üìß',
    color: 'linear-gradient(135deg, #f59e0b, #d97706)',
    border: '#fbbf24',
    points: 150,
    speed: 1.5,
    canShoot: false,
    movement: 'dive'
  },
  BAD_CONNECTION: {
    name: 'üì°',
    color: 'linear-gradient(135deg, #8b5cf6, #6d28d9)',
    border: '#a78bfa',
    points: 200,
    speed: 1,
    canShoot: true,
    movement: 'standard'
  },
  MALICIOUS_ADS: {
    name: 'üí∞',
    color: 'linear-gradient(135deg, #f97316, #db2777)',
    border: '#fb7185',
    points: 250,
    speed: 2.5,
    canShoot: true,
    movement: 'erratic'
  }
};

// Wave configurations
const WAVES = [
  {
    name: 'Malware Outbreak',
    enemies: [
      { type: 'MALWARE', count: 12, pattern: 'grid' }
    ]
  },
  {
    name: 'Phishing Campaign',
    enemies: [
      { type: 'PHISHING', count: 8, pattern: 'grid' },
      { type: 'MALWARE', count: 4, pattern: 'scattered' }
    ]
  },
  {
    name: 'Network Attack',
    enemies: [
      { type: 'BAD_CONNECTION', count: 6, pattern: 'grid' },
      { type: 'PHISHING', count: 6, pattern: 'scattered' }
    ]
  },
  {
    name: 'Full Assault',
    enemies: [
      { type: 'MALICIOUS_ADS', count: 4, pattern: 'scattered' },
      { type: 'BAD_CONNECTION', count: 4, pattern: 'grid' },
      { type: 'PHISHING', count: 4, pattern: 'scattered' },
      { type: 'MALWARE', count: 4, pattern: 'scattered' }
    ]
  }
];

function WaveMeter({ currentWave, totalWaves, lives, maxLives, score }) {
  const waveProgress = ((currentWave) / totalWaves) * 100;
  const waveName = currentWave <= totalWaves ? WAVES[currentWave - 1].name : 'Complete';

  return (
    <div style={{
        minWidth: "240px",
        padding: "1rem",
        borderRadius: "12px",
        border: "1px solid rgba(148,163,184,0.6)",
        background: "radial-gradient(circle at top, rgba(15,23,42,0.95), rgba(2,6,23,0.95))",
        boxShadow: "0 0 18px rgba(15,23,42,0.9)",
        display: "flex",
        flexDirection: "column",
        gap: "0.5rem",
      }}
    >
      <div style={{ display: "flex", alignItems: "center", gap: "0.5rem", marginBottom: "0.25rem" }}>
        <span style={{ fontSize: "1.9rem" }}>üõ°Ô∏è</span>
        <div style={{ fontSize: "0.9rem", lineHeight: 1.2 }}>
          <div style={{ fontWeight: "600" }}>Security Defense</div>
          <div style={{ opacity: 0.75 }}>Wave {Math.min(currentWave, totalWaves)} / {totalWaves}</div>
        </div>
      </div>

      <div style={{ height: "6px", borderRadius: "999px", overflow: "hidden", background: "rgba(30,64,175,0.65)", marginBottom: "0.25rem" }}>
        <div style={{ width: `${waveProgress}%`, height: "100%", background: "linear-gradient(90deg, #3b82f6, #06b6d4)", transition: "width 300ms ease" }} />
      </div>

      <div style={{ fontSize: "0.85rem", fontWeight: "600", opacity: 0.9, marginBottom: "0.5rem" }}>
        {waveName}
      </div>

      <div style={{ display: "flex", flexDirection: "column", gap: "0.35rem" }}>
        <div style={{ fontSize: "0.85rem", fontWeight: "600", opacity: 0.9 }}>Lives Remaining</div>
        <div style={{ display: "flex", gap: "0.3rem", flexWrap: "wrap" }}>
          {Array.from({ length: maxLives }).map((_, i) => (
            <div
              key={i}
              style={{
                width: "32px",
                height: "32px",
                borderRadius: "6px",
                border: "2px solid rgba(148,163,184,0.6)",
                background: i < lives ? "linear-gradient(135deg, #38bdf8, #1e40af)" : "rgba(15,23,42,0.5)",
                boxShadow: i < lives ? "0 0 10px rgba(56,189,248,0.5)" : "none",
                transition: "all 200ms ease",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontSize: "1.2rem",
              }}
            >
              {i < lives ? 'üöÄ' : ''}
            </div>
          ))}
        </div>
      </div>

      <div style={{ marginTop: "0.5rem", padding: "0.5rem", background: "rgba(59,130,246,0.1)", borderRadius: "6px", fontSize: "0.85rem" }}>
        <div style={{ fontWeight: "600", marginBottom: "0.25rem" }}>Score: {score}</div>
        <div style={{ opacity: 0.7, fontSize: "0.75rem" }}>Defend the network from cyber threats!</div>
      </div>
    </div>
  );
}

function createWaveEnemies(waveIndex) {
  const wave = WAVES[waveIndex];
  const enemies = [];
  let id = 0;

  wave.enemies.forEach((group) => {
    const type = ENEMY_TYPES[group.type];
    
    if (group.pattern === 'grid') {
      const cols = Math.min(group.count, 6);
      const rows = Math.ceil(group.count / cols);
      const spacing = 60;
      const startX = (GAME_WIDTH - (cols - 1) * spacing) / 2;
      const startY = 100 + (enemies.length > 0 ? 100 : 0);

      for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
          if (id >= group.count) break;
          enemies.push({
            id: id++,
            x: startX + col * spacing,
            y: startY + row * 50,
            type: group.type,
            typeData: type,
            alive: true,
            moveTimer: 0,
            shootTimer: Math.random() * 2000,
            diveTimer: 0,
            isDiving: false
          });
        }
      }
    } else {
      // Scattered pattern
      for (let i = 0; i < group.count; i++) {
        enemies.push({
          id: id++,
          x: Math.random() * (GAME_WIDTH - ENEMY_WIDTH - 100) + 50,
          y: Math.random() * 200 + 80,
          type: group.type,
          typeData: type,
          alive: true,
          moveTimer: 0,
          shootTimer: Math.random() * 2000,
          diveTimer: 0,
          isDiving: false
        });
      }
    }
  });

  return enemies;
}

export default function GalagaGame() {
  const navigate = useNavigate();

  const [playerX, setPlayerX] = useState(GAME_WIDTH / 2 - PLAYER_WIDTH / 2);
  const [playerY, setPlayerY] = useState(GAME_HEIGHT - 100);
  const [bullets, setBullets] = useState([]);
  const [enemyBullets, setEnemyBullets] = useState([]);
  const [enemies, setEnemies] = useState([]);
  const [keys, setKeys] = useState({ left: false, right: false, up: false, down: false, shoot: false });
  const [gameState, setGameState] = useState('playing');
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(PLAYER_LIVES);
  const [currentWave, setCurrentWave] = useState(1);
  const [isInvulnerable, setIsInvulnerable] = useState(false);

  const lastShotTimeRef = useRef(0);
  const shootCooldown = 250;
  const playerXRef = useRef(playerX);
  const playerYRef = useRef(playerY);

  useEffect(() => {
    playerXRef.current = playerX;
    playerYRef.current = playerY;
  }, [playerX, playerY]);

  useEffect(() => {
    if (enemies.length === 0 && gameState === 'playing') {
      setEnemies(createWaveEnemies(currentWave - 1));
    }
  }, []);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (gameState !== 'playing') return;
      
      if (e.key === 'ArrowLeft' || e.key === 'a' || e.key === 'A') {
        e.preventDefault();
        setKeys((k) => ({ ...k, left: true }));
      } else if (e.key === 'ArrowRight' || e.key === 'd' || e.key === 'D') {
        e.preventDefault();
        setKeys((k) => ({ ...k, right: true }));
      } else if (e.key === 'ArrowUp' || e.key === 'w' || e.key === 'W') {
        e.preventDefault();
        setKeys((k) => ({ ...k, up: true }));
      } else if (e.key === 'ArrowDown' || e.key === 's' || e.key === 'S') {
        e.preventDefault();
        setKeys((k) => ({ ...k, down: true }));
      } else if (e.key === ' ') {
        e.preventDefault();
        setKeys((k) => ({ ...k, shoot: true }));
      }
    };

    const handleKeyUp = (e) => {
      if (e.key === 'ArrowLeft' || e.key === 'a' || e.key === 'A') setKeys((k) => ({ ...k, left: false }));
      else if (e.key === 'ArrowRight' || e.key === 'd' || e.key === 'D') setKeys((k) => ({ ...k, right: false }));
      else if (e.key === 'ArrowUp' || e.key === 'w' || e.key === 'W') setKeys((k) => ({ ...k, up: false }));
      else if (e.key === 'ArrowDown' || e.key === 's' || e.key === 'S') setKeys((k) => ({ ...k, down: false }));
      else if (e.key === ' ') setKeys((k) => ({ ...k, shoot: false }));
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [gameState]);

  // Game loop
  useEffect(() => {
    if (gameState !== 'playing') return;

    const interval = setInterval(() => {
      // Move player
      setPlayerX((prevX) => {
        let x = prevX;
        if (keys.left && !keys.right) x -= PLAYER_SPEED;
        if (keys.right && !keys.left) x += PLAYER_SPEED;
        return Math.max(0, Math.min(x, GAME_WIDTH - PLAYER_WIDTH));
      });

      setPlayerY((prevY) => {
        let y = prevY;
        if (keys.up && !keys.down) y -= PLAYER_SPEED;
        if (keys.down && !keys.up) y += PLAYER_SPEED;
        return Math.max(GAME_HEIGHT / 2, Math.min(y, GAME_HEIGHT - PLAYER_HEIGHT - 20));
      });

      // Player shooting
      if (keys.shoot) {
        const now = performance.now();
        if (now - lastShotTimeRef.current > shootCooldown) {
          lastShotTimeRef.current = now;
          setBullets((prev) => [...prev, {
            x: playerXRef.current + PLAYER_WIDTH / 2 - BULLET_WIDTH / 2,
            y: playerYRef.current - 10
          }]);
        }
      }

      // Move bullets
      setBullets((prev) => prev.map((b) => ({ ...b, y: b.y - BULLET_SPEED })).filter((b) => b.y > 0));
      setEnemyBullets((prev) => prev.map((b) => ({ ...b, y: b.y + ENEMY_BULLET_SPEED })).filter((b) => b.y < GAME_HEIGHT));

      // Move and update enemies
      setEnemies((prevEnemies) => {
        return prevEnemies.map((enemy) => {
          if (!enemy.alive) return enemy;

          let newX = enemy.x;
          let newY = enemy.y;
          let newMoveTimer = enemy.moveTimer + TICK_MS;
          let newShootTimer = enemy.shootTimer - TICK_MS;
          let newDiveTimer = enemy.diveTimer - TICK_MS;
          let newIsDiving = enemy.isDiving;

          // Movement patterns
          if (enemy.typeData.movement === 'zigzag') {
            if (newMoveTimer > 500) {
              newX += Math.sin(Date.now() / 200) * enemy.typeData.speed * 2;
              newMoveTimer = 0;
            }
          } else if (enemy.typeData.movement === 'dive') {
            if (!newIsDiving && newDiveTimer <= 0 && Math.random() < 0.002) {
              newIsDiving = true;
              newDiveTimer = 2000;
            }
            if (newIsDiving) {
              newY += 3;
              newX += (playerXRef.current - enemy.x) * 0.02;
              if (newY > 400 || newDiveTimer <= 0) {
                newIsDiving = false;
                newDiveTimer = 3000 + Math.random() * 2000;
              }
            }
          } else if (enemy.typeData.movement === 'erratic') {
            newX += Math.sin(Date.now() / 150) * enemy.typeData.speed;
            newY += Math.cos(Date.now() / 300) * 0.5;
          } else {
            // Standard
            newX += Math.sin(Date.now() / 1000) * enemy.typeData.speed;
          }

          // Keep in bounds
          newX = Math.max(20, Math.min(newX, GAME_WIDTH - ENEMY_WIDTH - 20));
          newY = Math.max(50, Math.min(newY, GAME_HEIGHT / 2));

          // Enemy shooting
          if (enemy.typeData.canShoot && newShootTimer <= 0) {
            setEnemyBullets((prev) => [...prev, {
              x: enemy.x + ENEMY_WIDTH / 2 - BULLET_WIDTH / 2,
              y: enemy.y + ENEMY_HEIGHT
            }]);
            newShootTimer = 1500 + Math.random() * 1500;
          }

          return {
            ...enemy,
            x: newX,
            y: newY,
            moveTimer: newMoveTimer,
            shootTimer: newShootTimer,
            diveTimer: newDiveTimer,
            isDiving: newIsDiving
          };
        });
      });

      // Bullet collisions
      setBullets((prevBullets) => {
        let updatedEnemies = [...enemies];
        let updatedBullets = [...prevBullets];
        let hits = false;

        updatedBullets.forEach((bullet, bulletIndex) => {
          if (!bullet) return;
          updatedEnemies.forEach((enemy, enemyIndex) => {
            if (!enemy.alive) return;
            if (bullet.x < enemy.x + ENEMY_WIDTH &&
                bullet.x + BULLET_WIDTH > enemy.x &&
                bullet.y < enemy.y + ENEMY_HEIGHT &&
                bullet.y + BULLET_HEIGHT > enemy.y) {
              updatedEnemies[enemyIndex] = { ...enemy, alive: false };
              updatedBullets[bulletIndex] = null;
              setScore((prev) => prev + enemy.typeData.points);
              hits = true;
            }
          });
        });

        if (hits) setEnemies(updatedEnemies);
        
        // Check wave completion
        const aliveCount = updatedEnemies.filter((e) => e.alive).length;
        if (aliveCount === 0) {
          if (currentWave < WAVES.length) {
            setTimeout(() => {
              setCurrentWave((prev) => prev + 1);
              setEnemies(createWaveEnemies(currentWave));
            }, 1000);
          } else {
            setGameState('win');
          }
        }

        return updatedBullets.filter((b) => b !== null);
      });

      // Player collision with enemy bullets
      if (!isInvulnerable) {
        setEnemyBullets((prevBullets) => {
          let hit = false;
          const filtered = prevBullets.filter((bullet) => {
            if (bullet.x < playerXRef.current + PLAYER_WIDTH &&
                bullet.x + BULLET_WIDTH > playerXRef.current &&
                bullet.y < playerYRef.current + PLAYER_HEIGHT &&
                bullet.y + BULLET_HEIGHT > playerYRef.current) {
              hit = true;
              return false;
            }
            return true;
          });

          if (hit) {
            setLives((prev) => {
              const newLives = prev - 1;
              if (newLives <= 0) setGameState('lose');
              return newLives;
            });
            setIsInvulnerable(true);
            setTimeout(() => setIsInvulnerable(false), 2000);
          }

          return filtered;
        });
      }

      // Player collision with enemies
      if (!isInvulnerable) {
        const playerCollision = enemies.some((enemy) => {
          if (!enemy.alive) return false;
          return (
            playerXRef.current < enemy.x + ENEMY_WIDTH &&
            playerXRef.current + PLAYER_WIDTH > enemy.x &&
            playerYRef.current < enemy.y + ENEMY_HEIGHT &&
            playerYRef.current + PLAYER_HEIGHT > enemy.y
          );
        });

        if (playerCollision) {
          setLives((prev) => {
            const newLives = prev - 1;
            if (newLives <= 0) setGameState('lose');
            return newLives;
          });
          setIsInvulnerable(true);
          setTimeout(() => setIsInvulnerable(false), 2000);
        }
      }
    }, TICK_MS);

    return () => clearInterval(interval);
  }, [keys, gameState, enemies, currentWave, isInvulnerable]);

  const handleRestart = () => {
    setPlayerX(GAME_WIDTH / 2 - PLAYER_WIDTH / 2);
    setPlayerY(GAME_HEIGHT - 100);
    setBullets([]);
    setEnemyBullets([]);
    setEnemies(createWaveEnemies(0));
    setGameState('playing');
    setScore(0);
    setLives(PLAYER_LIVES);
    setCurrentWave(1);
    setIsInvulnerable(false);
    lastShotTimeRef.current = 0;
  };

  const handleExit = () => navigate(DASHBOARD_ROUTE);

  return (
    <div style={{ minHeight: '100vh', background: 'radial-gradient(circle at top, #0f172a 0, #020617 50%, #000 100%)', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', color: 'white', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif', padding: '1.5rem', gap: '1.25rem', position: 'relative' }}>
      <button onClick={handleExit} style={{ position: 'absolute', top: '1rem', right: '1rem', background: 'rgba(0,0,0,0.6)', borderRadius: '999px', border: '1px solid rgba(255,255,255,0.3)', color: 'white', padding: '0.5rem 0.9rem', cursor: 'pointer', fontSize: '0.9rem', fontWeight: '500' }}>‚úï Exit</button>

      <h1 style={{ fontSize: '2.5rem', textAlign: 'center', marginBottom: '0.25rem' }}>üõ°Ô∏è Cyber Defense Force</h1>
      <p style={{ opacity: 0.8, textAlign: 'center', maxWidth: 600, fontSize: '1rem' }}>
        Move with <strong>WASD / Arrows</strong>, shoot with <strong>Space</strong>. Eliminate all cyber threats!
      </p>

      <div style={{ display: 'flex', gap: '1.5rem', alignItems: 'flex-start', flexWrap: 'wrap', justifyContent: 'center' }}>
        <div style={{ position: 'relative', width: GAME_WIDTH, height: GAME_HEIGHT, background: 'linear-gradient(180deg, #020617 0%, #020617 60%, #000 100%)', border: '4px solid #4b5563', borderRadius: '12px', overflow: 'hidden', boxShadow: '0 0 32px rgba(0,0,0,0.9)' }}>
          
          {/* Player */}
          <div style={{ position: 'absolute', width: PLAYER_WIDTH, height: PLAYER_HEIGHT, left: playerX, top: playerY, background: 'linear-gradient(135deg, #38bdf8, #1e40af)', borderRadius: '8px', boxShadow: isInvulnerable ? '0 0 30px rgba(239,68,68,0.9)' : '0 0 20px rgba(56,189,248,0.9)', border: '2px solid #0ea5e9', opacity: isInvulnerable && Date.now() % 200 < 100 ? 0.5 : 1, transition: 'opacity 100ms' }}>
            <div style={{ width: 0, height: 0, borderLeft: `${PLAYER_WIDTH / 2}px solid transparent`, borderRight: `${PLAYER_WIDTH / 2}px solid transparent`, borderBottom: '14px solid #bfdbfe', position: 'absolute', top: -14, left: 0 }} />
          </div>

          {/* Player bullets */}
          {bullets.map((b, i) => (
            <div key={i} style={{ position: 'absolute', width: BULLET_WIDTH, height: BULLET_HEIGHT, left: b.x, top: b.y, background: 'linear-gradient(180deg, #facc15, #f97316)', boxShadow: '0 0 12px rgba(250,204,21,0.95)', borderRadius: '999px' }} />
          ))}

          {/* Enemy bullets */}
          {enemyBullets.map((b, i) => (
            <div key={i} style={{ position: 'absolute', width: BULLET_WIDTH, height: BULLET_HEIGHT, left: b.x, top: b.y, background: 'linear-gradient(180deg, #dc2626, #991b1b)', boxShadow: '0 0 12px rgba(220,38,38,0.95)', borderRadius: '999px' }} />
          ))}

          {/* Enemies */}
          {enemies.map((e) => e.alive && (
            <div key={e.id} style={{ position: 'absolute', width: ENEMY_WIDTH, height: ENEMY_HEIGHT, left: e.x, top: e.y, background: e.typeData.color, borderRadius: '6px', border: `2px solid ${e.typeData.border}`, boxShadow: `0 0 14px ${e.typeData.border}99`, display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '1.8rem' }}>
              {e.typeData.name}
            </div>
          ))}

          {/* End game overlay */}
          {gameState !== 'playing' && (
            <div style={{ position: 'absolute', inset: 0, background: 'rgba(0,0,0,0.85)', backdropFilter: 'blur(4px)', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', gap: '1rem', textAlign: 'center', padding: '1.5rem' }}>
              <h2 style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>{gameState === 'win' ? 'üéâ NETWORK SECURED!' : 'üíÄ SYSTEM COMPROMISED'}</h2>
              <p style={{ opacity: 0.9, fontSize: '1.1rem', maxWidth: '400px' }}>
                {gameState === 'win' ? 'All cyber threats eliminated! The network is safe.' : 'The threats breached your defenses. Regroup and try again.'}
              </p>
              <div style={{ fontSize: '1.2rem', fontWeight: 'bold', marginTop: '0.5rem' }}>Final Score: {score}</div>
              <div style={{ display: 'flex', gap: '0.75rem', marginTop: '0.75rem' }}>
                <button onClick={handleRestart} style={{ padding: '0.8rem 1.5rem', borderRadius: '999px', border: 'none', background: 'linear-gradient(135deg, #3b82f6, #1e40af)', color: 'white', fontWeight: 'bold', cursor: 'pointer', fontSize: '1rem', boxShadow: '0 4px 12px rgba(59, 130, 246, 0.4)' }}>Play Again</button>
                <button onClick={handleExit} style={{ padding: '0.8rem 1.5rem', borderRadius: '999px', border: '1px solid rgba(148,163,184,0.6)', background: 'rgba(15,23,42,0.9)', color: 'white', cursor: 'pointer', fontSize: '1rem' }}>Dashboard</button>
              </div>
            </div>
          )}
        </div>

        <WaveMeter currentWave={currentWave} totalWaves={WAVES.length} lives={lives} maxLives={PLAYER_LIVES} score={score} />
      </div>

      {gameState === 'playing' && (
        <button onClick={handleRestart} style={{ marginTop: '0.5rem', padding: '0.5rem 1.2rem', borderRadius: '999px', border: '1px solid rgba(148,163,184,0.6)', background: 'rgba(15,23,42,0.7)', color: 'white', fontSize: '0.9rem', cursor: 'pointer' }}>Restart</button>
      )}
    </div>
  );
}
