import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, Star, Trophy, Zap, Play, Lock, Home, LogOut, X } from 'lucide-react';

export default function GameDashboard() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedArena, setSelectedArena] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('cyberslayers_user');
    if (storedUser) {
      setUserData(JSON.parse(storedUser));
    } else {
      navigate('/login');
    }
    setLoading(false);
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('cyberslayers_user');
    navigate('/');
  };

  const getProgressColor = (progress) => {
    if (progress <= 25) return '#d32f2f';
    if (progress <= 50) return '#f57c00';  
    if (progress <= 75) return '#fbc02d';
    return '#388e3c';
  };

  const getGameStatus = (score) => {
    if (score === 0) return { status: 'locked', color: '#78716c', icon: Lock };
    if (score <= 300) return { status: 'bronze', color: '#cd7f32', icon: Star };
    if (score <= 600) return { status: 'silver', color: '#c0c0c0', icon: Star };
    if (score <= 800) return { status: 'gold', color: '#ffd700', icon: Trophy };
    return { status: 'diamond', color: '#00bcd4', icon: Zap };
  };

  const handleArenaClick = (arenaData) => {
    if (!arenaData.isLocked) {
      setSelectedArena(arenaData);
      setShowModal(true);
    }
  };

  const handlePlayGame = () => {
    console.log(`Starting ${selectedArena.name}...`);
    setShowModal(false);
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #8b4513 0%, #deb887 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#4a3728',
        fontSize: '1.5rem',
        fontFamily: 'serif'
      }}>
        üìú Unrolling your adventure map... üó∫Ô∏è
      </div>
    );
  }

  if (!userData) return null;

  const progressColor = getProgressColor(userData.overall_game_progress);
  const games = userData.games || {};

  // Journey locations positioned within the map card
  const journeyStops = [
    {
      gameNum: 1,
      name: "Password Candy Castle",
      shortName: "Candy Castle",
      description: "Learn to forge the strongest password shields in the candy kingdom!",
      emoji: "üè∞",
      position: { top: '20%', left: '15%' }, // Start position
      color: '#e91e63',
      bgColor: '#fce4ec'
    },
    {
      gameNum: 2,
      name: "Phishing Gingerbread Village", 
      shortName: "Gingerbread Village",
      description: "Spot the tricky treats and suspicious sweetness in this village!",
      emoji: "üç™",
      position: { top: '45%', left: '40%' }, // Mid-left
      color: '#ff9800',
      bgColor: '#fff3e0'
    },
    {
      gameNum: 3,
      name: "Malware Lollipop Forest",
      shortName: "Lollipop Forest", 
      description: "Navigate through the sweet but dangerous candy woods!",
      emoji: "üç≠",
      position: { top: '30%', left: '70%' }, // Top-right
      color: '#4caf50',
      bgColor: '#e8f5e8'
    },
    {
      gameNum: 4,
      name: "Network Ice Cream Palace",
      shortName: "Ice Cream Palace",
      description: "Defend the sweetest networks at the journey's end!",
      emoji: "üç¶",
      position: { top: '75%', left: '80%' }, // End position
      color: '#2196f3',
      bgColor: '#e3f2fd'
    }
  ];

  const stopsWithData = journeyStops.map(stop => {
    const scoreField = stop.gameNum === 3 ? 'game3_scroe' : `game${stop.gameNum}_score`;
    const score = games[scoreField] || 0;
    const gameStatus = getGameStatus(score);
    const isLocked = stop.gameNum > 1 && (
      stop.gameNum === 2 ? games['game1_score'] === 0 : 
      stop.gameNum === 3 ? games['game2_score'] === 0 : 
      stop.gameNum === 4 ? games['game3_scroe'] === 0 : false
    );

    return { ...stop, score, gameStatus, isLocked };
  });

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      fontFamily: 'Arial, sans-serif',
      padding: '1rem'
    },
    
    // Navbar
    navbar: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(20px)',
      borderRadius: '15px',
      padding: '1rem 1.5rem',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      marginBottom: '1rem'
    },
    userInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem'
    },
    userName: {
      fontSize: '1.3rem',
      fontWeight: 'bold'
    },
    navButtons: {
      display: 'flex',
      gap: '0.5rem'
    },
    navButton: {
      padding: '0.5rem 1rem',
      background: 'rgba(255, 255, 255, 0.2)',
      border: 'none',
      borderRadius: '10px',
      color: 'white',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontSize: '0.9rem',
      fontWeight: 'bold'
    },

    // Progress Bar Section
    progressSection: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(20px)',
      borderRadius: '15px',
      padding: '1.5rem',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      textAlign: 'center',
      marginBottom: '1rem'
    },
    progressTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '1rem',
      textShadow: '0 2px 4px rgba(0,0,0,0.3)'
    },
    progressBarContainer: {
      width: '100%',
      height: '20px',
      background: 'rgba(255, 255, 255, 0.2)',
      borderRadius: '10px',
      overflow: 'hidden',
      position: 'relative',
      boxShadow: 'inset 0 2px 4px rgba(0,0,0,0.2)'
    },
    progressBar: {
      height: '100%',
      background: `linear-gradient(90deg, ${progressColor}, ${progressColor}dd)`,
      borderRadius: '10px',
      transition: 'width 1.5s ease-in-out'
    },
    progressText: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      fontWeight: 'bold',
      fontSize: '0.9rem',
      color: 'white',
      textShadow: '0 1px 2px rgba(0,0,0,0.8)'
    },
    progressMessage: {
      marginTop: '0.5rem',
      fontSize: '1rem',
      opacity: 0.9
    },

    // Dedicated Map Card
    mapCard: {
      background: `
        linear-gradient(45deg, rgba(139, 69, 19, 0.1) 0%, rgba(222, 184, 135, 0.1) 100%),
        rgba(244, 241, 232, 0.95)
      `,
      backdropFilter: 'blur(20px)',
      borderRadius: '20px',
      padding: '2rem',
      border: '3px solid #d4af37',
      boxShadow: '0 8px 32px rgba(139, 69, 19, 0.3)',
      position: 'relative',
      minHeight: '500px',
      color: '#4a3728'
    },
    mapTitle: {
      textAlign: 'center',
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#8b4513',
      marginBottom: '2rem',
      textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
    },
    mapContainer: {
      position: 'relative',
      height: '400px',
      background: `
        radial-gradient(circle at 15% 20%, rgba(233, 30, 99, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 40% 45%, rgba(255, 152, 0, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 70% 30%, rgba(76, 175, 80, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 80% 75%, rgba(33, 150, 243, 0.05) 0%, transparent 50%)
      `,
      borderRadius: '15px',
      border: '2px dashed #d4af37',
      overflow: 'hidden'
    },
    pathSvg: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      zIndex: 1
    },
    journeyStop: {
      position: 'absolute',
      width: '90px',
      height: '90px',
      borderRadius: '50%',
      cursor: 'pointer',
      transition: 'all 0.4s ease',
      zIndex: 10,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      border: '4px solid #d4af37',
      boxShadow: '0 6px 16px rgba(139, 69, 19, 0.4)',
      fontSize: '2.5rem'
    },
    stopLocked: {
      filter: 'grayscale(100%) brightness(0.7)',
      cursor: 'not-allowed',
      opacity: 0.6
    },
    stopHover: {
      transform: 'scale(1.15)',
      boxShadow: '0 10px 25px rgba(139, 69, 19, 0.6)',
      zIndex: 20
    },
    stopLabel: {
      position: 'absolute',
      bottom: '-35px',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'rgba(244, 241, 232, 0.95)',
      color: '#8b4513',
      padding: '0.3rem 0.8rem',
      borderRadius: '8px',
      fontSize: '0.8rem',
      fontWeight: 'bold',
      whiteSpace: 'nowrap',
      border: '2px solid #d4af37',
      textAlign: 'center'
    },
    stopNumber: {
      position: 'absolute',
      top: '-8px',
      left: '-8px',
      width: '24px',
      height: '24px',
      borderRadius: '50%',
      background: '#8b4513',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '0.8rem',
      fontWeight: 'bold',
      border: '2px solid white'
    },

    // Modal styles
    modal: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(139, 69, 19, 0.8)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    },
    modalContent: {
      background: 'linear-gradient(135deg, #f4f1e8 0%, #deb887 100%)',
      borderRadius: '20px',
      padding: '2rem',
      maxWidth: '500px',
      width: '90%',
      color: '#4a3728',
      position: 'relative',
      border: '4px solid #d4af37',
      boxShadow: '0 8px 24px rgba(139, 69, 19, 0.6)'
    },
    modalClose: {
      position: 'absolute',
      top: '1rem',
      right: '1rem',
      background: '#cd853f',
      border: '2px solid #d4af37',
      borderRadius: '50%',
      width: '40px',
      height: '40px',
      color: 'white',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    modalHeader: {
      textAlign: 'center',
      marginBottom: '1.5rem'
    },
    modalEmoji: {
      fontSize: '4rem',
      marginBottom: '1rem'
    },
    modalTitle: {
      fontSize: '1.8rem',
      fontWeight: 'bold',
      marginBottom: '0.5rem',
      color: '#8b4513'
    },
    modalDescription: {
      fontSize: '1rem',
      color: '#5d4e37',
      fontStyle: 'italic'
    },
    modalStats: {
      background: 'rgba(139, 69, 19, 0.1)',
      borderRadius: '12px',
      padding: '1rem',
      marginBottom: '1.5rem',
      border: '2px solid #d4af37'
    },
    modalButtons: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'center'
    },
    modalButton: {
      padding: '1rem 1.5rem',
      border: 'none',
      borderRadius: '10px',
      fontWeight: 'bold',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem'
    },
    playButton: {
      background: 'linear-gradient(45deg, #4caf50, #66bb6a)',
      color: 'white',
      border: '2px solid #388e3c'
    },
    cancelButton: {
      background: '#cd853f',
      color: 'white',
      border: '2px solid #d4af37'
    }
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <div style={styles.navbar}>
        <div style={styles.userInfo}>
          <Shield size={32} color="#00bcd4" />
          <div>
            <div style={styles.userName}>
              {userData.email.split('@')[0]}'s Adventure
            </div>
            <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>
              Level {Math.floor(userData.overall_game_progress / 25) + 1} Cyber Explorer
            </div>
          </div>
        </div>
        <div style={styles.navButtons}>
          <button 
            style={styles.navButton}
            onClick={() => navigate('/')}
            onMouseEnter={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.3)'}
            onMouseLeave={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.2)'}
          >
            <Home size={16} />
            Home
          </button>
          <button 
            style={styles.navButton}
            onClick={handleLogout}
            onMouseEnter={(e) => e.target.style.background = 'rgba(255, 69, 58, 0.3)'}
            onMouseLeave={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.2)'}
          >
            <LogOut size={16} />
            Logout
          </button>
        </div>
      </div>

      {/* Quest Progress Bar */}
      <div style={styles.progressSection}>
        <div style={styles.progressTitle}>
          üõ°Ô∏è Your Cyber Adventure Progress üõ°Ô∏è
        </div>
        <div style={styles.progressBarContainer}>
          <div 
            style={{
              ...styles.progressBar,
              width: `${userData.overall_game_progress}%`
            }}
          />
          <div style={styles.progressText}>
            {userData.overall_game_progress}% Complete
          </div>
        </div>
        <div style={styles.progressMessage}>
          {userData.overall_game_progress < 25 && "üå± Just getting started! Keep going!"}
          {userData.overall_game_progress >= 25 && userData.overall_game_progress < 50 && "üöÄ Great progress! You're on fire!"}
          {userData.overall_game_progress >= 50 && userData.overall_game_progress < 75 && "‚≠ê Amazing work! Almost there!"}
          {userData.overall_game_progress >= 75 && userData.overall_game_progress < 100 && "üèÜ So close to mastery!"}
          {userData.overall_game_progress >= 100 && "üëë Cyber Security Master!"}
        </div>
      </div>

      {/* Dedicated Map Card */}
      <div style={styles.mapCard}>
        <div style={styles.mapTitle}>
          üó∫Ô∏è Candy Land Cyber Quest Journey üó∫Ô∏è
        </div>
        
        <div style={styles.mapContainer}>
          {/* Curved Journey Path */}
          <svg style={styles.pathSvg}>
            <defs>
              <linearGradient id="journeyPath" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" stopColor="#e91e63" stopOpacity="0.8" />
                <stop offset="33%" stopColor="#ff9800" stopOpacity="0.8" />
                <stop offset="66%" stopColor="#4caf50" stopOpacity="0.8" />
                <stop offset="100%" stopColor="#2196f3" stopOpacity="0.8" />
              </linearGradient>
            </defs>
            <path
              d="M 15% 20% 
                 C 25% 35%, 30% 40%, 40% 45%
                 C 55% 50%, 60% 35%, 70% 30%
                 C 75% 45%, 78% 60%, 80% 75%"
              stroke="url(#journeyPath)"
              strokeWidth="8"
              fill="none"
              strokeDasharray="15,10"
              strokeLinecap="round"
              opacity="0.9"
            />
          </svg>

          {/* Journey Stops */}
          {stopsWithData.map((stop) => (
            <JourneyStop
              key={stop.gameNum}
              stop={stop}
              onClick={() => handleArenaClick(stop)}
              styles={styles}
            />
          ))}
        </div>
      </div>

      {/* Game Modal */}
      {showModal && selectedArena && (
        <div style={styles.modal} onClick={() => setShowModal(false)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <button style={styles.modalClose} onClick={() => setShowModal(false)}>
              <X size={20} />
            </button>
            
            <div style={styles.modalHeader}>
              <div style={styles.modalEmoji}>{selectedArena.emoji}</div>
              <div style={styles.modalTitle}>{selectedArena.name}</div>
              <div style={styles.modalDescription}>{selectedArena.description}</div>
            </div>

            <div style={styles.modalStats}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Sweet Score:</span>
                <span style={{ fontWeight: 'bold', color: selectedArena.gameStatus.color }}>
                  {selectedArena.score}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Status:</span>
                <span style={{ fontWeight: 'bold', color: selectedArena.gameStatus.color }}>
                  {selectedArena.gameStatus.status.toUpperCase()}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span>Progress:</span>
                <span style={{ fontWeight: 'bold' }}>
                  {selectedArena.score > 0 ? `${Math.min(Math.floor(selectedArena.score / 10), 100)}%` : '0%'}
                </span>
              </div>
            </div>

            <div style={styles.modalButtons}>
              <button 
                style={{...styles.modalButton, ...styles.playButton}}
                onClick={handlePlayGame}
                onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
                onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
              >
                <Play size={20} />
                Begin Quest!
              </button>
              <button 
                style={{...styles.modalButton, ...styles.cancelButton}}
                onClick={() => setShowModal(false)}
              >
                Later
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// Journey Stop Component
function JourneyStop({ stop, onClick, styles }) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      style={{
        ...styles.journeyStop,
        ...stop.position,
        background: `linear-gradient(135deg, ${stop.bgColor}, ${stop.color}44)`,
        ...(stop.isLocked ? styles.stopLocked : {}),
        ...(isHovered && !stop.isLocked ? styles.stopHover : {})
      }}
      onClick={onClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {stop.isLocked ? 'üîí' : stop.emoji}
      
      {/* Stop Number */}
      <div style={styles.stopNumber}>
        {stop.gameNum}
      </div>
      
      {/* Stop Label */}
      <div style={styles.stopLabel}>
        {stop.shortName}
      </div>
      
      {/* Status badge */}
      <div style={{
        position: 'absolute',
        top: '-8px',
        right: '-8px',
        width: '24px',
        height: '24px',
        borderRadius: '50%',
        background: stop.gameStatus.color,
        border: '2px solid white',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <stop.gameStatus.icon size={12} color="white" />
      </div>
    </div>
  );
}
